CREATE OR REPLACE FUNCTION tiss_obter_formato_red_acresc ( cd_convenio_p bigint, cd_estabelecimento_p bigint, vl_reducao_acrescimo_p bigint) RETURNS bigint AS $body$
DECLARE

vl_retorno_w		double precision;
vl_reducao_acrescimo_w	double precision;
ie_formato_red_acres_w	varchar(255);


BEGIN

select	coalesce(max(ie_formato_red_acres), 'V')
into STRICT	ie_formato_red_acres_w
from	tiss_parametros_convenio
where	cd_estabelecimento	= cd_estabelecimento_p
and	cd_convenio		= cd_convenio_p;

vl_reducao_acrescimo_w	:= vl_reducao_acrescimo_p;

if (coalesce(ie_formato_red_acres_w,'V') = 'V') then
	vl_reducao_acrescimo_w	:= trunc((vl_reducao_acrescimo_w / 100),2);
elsif (coalesce(ie_formato_red_acres_w,'V') = 'C') then
	vl_reducao_acrescimo_w	:= (100 - (vl_reducao_acrescimo_w * 100));
elsif (coalesce(ie_formato_red_acres_w,'V') = 'F') then
	vl_reducao_acrescimo_w	:= ((vl_reducao_acrescimo_w * 100) - 100);
else
	vl_reducao_acrescimo_w	:= vl_reducao_acrescimo_p;
end if;

vl_retorno_w	:= vl_reducao_acrescimo_w;

return	vl_retorno_w;

end	;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
