CREATE OR REPLACE FUNCTION tiss_obter_guia_outras_desp (nr_interno_conta_p bigint, cd_autorizacao_p text) RETURNS varchar AS $body$
DECLARE

 
ds_retorno_w	varchar(100);
cont_w		bigint;


BEGIN 
 
select	count(*) 
into STRICT	cont_w 
from	tiss_guias_resumo_internacao_v 
where	nr_interno_conta	= nr_interno_conta_p 
and	cd_autorizacao		= cd_autorizacao_p;
 
if (cont_w > 0) then 
	ds_retorno_w	:= '5';	-- Gerar na guia de resumo de internação 
else
	ds_retorno_w	:= '4';	-- Gerar na guia de SPSADT 
end if;
 
return	ds_retorno_w;
 
end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
