SET client_encoding TO 'UTF8';





CREATE OR REPLACE FUNCTION tiss_obter_guia_prest_proc (nr_sequencia_p bigint, nr_seq_partic_p bigint, ie_honorario_p text) RETURNS varchar AS $body$
DECLARE


/*
Esta function e responsavel por retornar o numero de guia do prestador quando procedimento.
Existe esta function, pois nao podemos tratar diretamente na TISS_GERAR_CONTA_GUIA, pois o numero de guia prestador deve ser gravado para o procedimento.
*/
					
ds_retorno_w		varchar(20)	:= null;
ie_guia_prestador_w	varchar(10);
cd_convenio_w		bigint;
cd_estabelecimento_w	bigint;
nr_atendimento_w	bigint;
cd_procedimento_w	bigint;
nr_seq_proc_autor_w	bigint;
ie_tiss_tipo_guia_w	varchar(20);
ie_forma_w		varchar(3);
cd_senha_w		procedimento_paciente.cd_senha%type;
ie_tiss_tipo_guia_honor_w	varchar(20);
nr_interno_conta_w 	varchar(10);
nr_seq_protocolo_w 	bigint;
nr_seq_proc_interno_w	proc_interno.nr_sequencia%type;
cd_autorizacao_w	procedimento_paciente.nr_doc_convenio%type;

c01 CURSOR FOR
SELECT	ie_forma
from	tiss_regra_guia_prest
where	cd_estabelecimento					= cd_estabelecimento_w
and	cd_convenio						= cd_convenio_w
and	coalesce(ie_tiss_tipo_guia,coalesce(ie_tiss_tipo_guia_w,'X'))	= coalesce(ie_tiss_tipo_guia_w,'X')
order by coalesce(ie_tiss_tipo_guia,'X');


BEGIN

begin
select	b.cd_convenio_parametro,
	b.cd_estabelecimento,
	b.nr_atendimento,
	a.nr_seq_proc_autor,
	a.cd_procedimento,
	ie_tiss_tipo_guia,
	a.cd_senha,
	a.ie_tiss_tipo_guia_honor,
	b.nr_interno_conta,
	coalesce(a.nr_seq_proc_interno,0),
	a.nr_doc_convenio
into STRICT	cd_convenio_w,
	cd_estabelecimento_w,
	nr_atendimento_w,
	nr_seq_proc_autor_w,
	cd_procedimento_w,
	ie_tiss_tipo_guia_w,
	cd_senha_w,
	ie_tiss_tipo_guia_honor_w,
	nr_interno_conta_w,
	nr_seq_proc_interno_w,
	cd_autorizacao_w
from	conta_paciente b,
	procedimento_paciente a
where	a.nr_interno_conta	= b.nr_interno_conta
and	a.nr_sequencia		= nr_sequencia_p  LIMIT 1;
exception
when others then
	null;
end;

select	coalesce(max(ie_guia_prestador),'C')
into STRICT	ie_guia_prestador_w
from	tiss_parametros_convenio
where	cd_convenio		= cd_convenio_w
and	cd_estabelecimento	= cd_estabelecimento_w;

if (ie_guia_prestador_w = 'R') then

	open C01;
	loop
	fetch C01 into
		ie_forma_w;
	EXIT WHEN NOT FOUND; /* apply on C01 */
	end loop;
	close C01;

	if (ie_forma_w = '13') then --Guia do procedimento na autorizacao (Autorizado)
		begin
			select	a.cd_autorizacao
			into STRICT	ds_retorno_w
			from	autorizacao_convenio a,
				procedimento_autorizado b,
				estagio_autorizacao c
			where	a.nr_seq_estagio	= c.nr_sequencia
			and	a.nr_sequencia		= b.nr_sequencia_autor
			and	b.nr_sequencia		= nr_seq_proc_autor_w
			and	a.nr_atendimento	= nr_atendimento_w
			and	a.cd_convenio		= cd_convenio_w
			and	coalesce(a.cd_senha,'X')	= coalesce(cd_senha_w,coalesce(a.cd_senha,'X'))
			and	c.ie_interno		= '10' --Somente se estiver autorizado.
  LIMIT 1;
		exception
		when no_data_found then
			begin
				select	a.cd_autorizacao
				into STRICT	ds_retorno_w
				from	autorizacao_convenio a,
					procedimento_autorizado b,
					estagio_autorizacao c
				where	a.nr_seq_estagio	= c.nr_sequencia
				and	a.nr_sequencia		= b.nr_sequencia_autor
				and	b.cd_procedimento	= cd_procedimento_w
				and	a.nr_atendimento	= nr_atendimento_w
				and	a.cd_convenio		= cd_convenio_w
				and	coalesce(a.cd_senha,'X')	= coalesce(cd_senha_w,coalesce(a.cd_senha,'X'))
				and	c.ie_interno		= '10' --Somente se estiver autorizado.
  LIMIT 1;
			exception
			when others then
				ds_retorno_w	:= null;
			end;
		end;

	elsif (ie_forma_w = '14') then --Numero guia da EUP ou Guia do procedimento na autorizacao (Autorizado)
		begin
			select	a.cd_autorizacao
			into STRICT	ds_retorno_w
			from	autorizacao_convenio a,
				procedimento_autorizado b,
				estagio_autorizacao c
			where	a.nr_seq_estagio	= c.nr_sequencia
			and	a.nr_sequencia		= b.nr_sequencia_autor
			and	b.nr_sequencia		= nr_seq_proc_autor_w
			and	a.nr_atendimento	= nr_atendimento_w
			and	a.cd_convenio		= cd_convenio_w
			and	coalesce(a.cd_senha,'X')	= coalesce(cd_senha_w,coalesce(a.cd_senha,'X'))
			and	c.ie_interno		= '10' --Somente se estiver autorizado.
  LIMIT 1;
		exception
		when no_data_found then
			begin
				select	a.cd_autorizacao
				into STRICT	ds_retorno_w
				from	autorizacao_convenio a,
					procedimento_autorizado b,
					estagio_autorizacao c
				where	a.nr_seq_estagio	= c.nr_sequencia
				and	a.nr_sequencia		= b.nr_sequencia_autor
				and	b.cd_procedimento	= cd_procedimento_w
				and	a.nr_atendimento	= nr_atendimento_w
				and	a.cd_convenio		= cd_convenio_w
				and	coalesce(a.cd_senha,'X')	= coalesce(cd_senha_w,coalesce(a.cd_senha,'X'))
				and	c.ie_interno		= '10' --Somente se estiver autorizado.
  LIMIT 1;
			exception
			when others then
				ds_retorno_w	:= null;
			end;
		end;
		
		if (coalesce(ds_retorno_w::text, '') = '') then
			begin
				select	b.nr_doc_convenio
				into STRICT	ds_retorno_w
				from	atend_categoria_convenio b,
					atendimento_paciente a
				where	a.nr_atendimento		= b.nr_atendimento
				and	b.nr_seq_interno		= obter_atecaco_atend_conv(a.nr_atendimento,cd_convenio_w)
				and	a.nr_atendimento		= nr_atendimento_w  LIMIT 1;
			exception
			when others then
				ds_retorno_w	:= null;
			end;
		end if;

	elsif (ie_forma_w = '15') then --Intervalor de numeracao por guia operadora
		if (nr_seq_partic_p IS NOT NULL AND nr_seq_partic_p::text <> '') then
			begin
			select	substr(t.vl_campo,1,20)
			into STRICT	ds_retorno_w
			from	proc_paciente_tiss t
			where	t.nr_seq_procedimento	= nr_sequencia_p
			and	t.nr_seq_partic		= nr_seq_partic_p
			and	t.ie_tiss_tipo_guia	= ie_tiss_tipo_guia_w
			and	t.ds_campo		= 'NR_GUIA_PRESTADOR'  LIMIT 1;
			exception
			when others then
				ds_retorno_w		:= null;
			end;

		else
			if (ie_honorario_p = 'S') then
				begin
				select	substr(t.vl_campo,1,20)
				into STRICT	ds_retorno_w
				from	proc_paciente_tiss t
				where	t.nr_seq_procedimento	= nr_sequencia_p
				and	t.ie_tiss_tipo_guia	= ie_tiss_tipo_guia_honor_w
				and	t.ds_campo		= 'NR_GUIA_PRESTADOR_HONOR'  LIMIT 1;
				exception
				when others then
					begin
					select	substr(t.vl_campo,1,20)
					into STRICT	ds_retorno_w
					from	proc_paciente_tiss t
					where	t.nr_seq_procedimento	= nr_sequencia_p
					and	t.ie_tiss_tipo_guia	= ie_tiss_tipo_guia_w
					and	t.ds_campo		= 'NR_GUIA_PRESTADOR'  LIMIT 1;
					exception
					when others then
						ds_retorno_w		:= null;
					end;
				end;
			else
				begin
				select	substr(t.vl_campo,1,20)
				into STRICT	ds_retorno_w
				from	proc_paciente_tiss t
				where	t.nr_seq_procedimento	= nr_sequencia_p
				and	t.ie_tiss_tipo_guia	= ie_tiss_tipo_guia_w
				and	t.ds_campo		= 'NR_GUIA_PRESTADOR'  LIMIT 1;
				exception
				when others then
					ds_retorno_w		:= null;
				end;
			end if;
		end if;
	elsif (ie_forma_w = '16') then --Guia prestador da autorizacao convenio atraves do procedimento
		begin
		select	a.cd_autorizacao_prest
		into STRICT	ds_retorno_w
		from	autorizacao_convenio a,
			procedimento_autorizado b,
			estagio_autorizacao c
		where	a.nr_seq_estagio	= c.nr_sequencia
		and	a.nr_sequencia		= b.nr_sequencia_autor
		and	b.nr_sequencia		= nr_seq_proc_autor_w
		and	a.nr_atendimento	= nr_atendimento_w
		and	a.cd_convenio		= cd_convenio_w
		and	coalesce(a.cd_senha,'X')	= coalesce(cd_senha_w,coalesce(a.cd_senha,'X'))  LIMIT 1;
		exception
		when no_data_found then
			begin
			select	a.cd_autorizacao_prest
			into STRICT	ds_retorno_w
			from	autorizacao_convenio a,
				procedimento_autorizado b,
				estagio_autorizacao c
			where	a.nr_seq_estagio	= c.nr_sequencia
			and	a.nr_sequencia		= b.nr_sequencia_autor
			and	b.cd_procedimento	= cd_procedimento_w
			and	coalesce(b.nr_seq_proc_interno, nr_seq_proc_interno_w) = nr_seq_proc_interno_w
			and	a.nr_atendimento	= nr_atendimento_w
			and	a.cd_convenio		= cd_convenio_w
			and	coalesce(a.cd_senha,'X')	= coalesce(cd_senha_w,coalesce(a.cd_senha,'X'))  LIMIT 1;
			exception
			when others then
				ds_retorno_w	:= null;
			end;
		end;
	elsif (ie_forma_w = '19') then --Numero da conta + Procolo convenio
        	begin
            		if (nr_interno_conta_w IS NOT NULL AND nr_interno_conta_w::text <> '') then
               			nr_seq_protocolo_w := obter_nr_seq_protocolo(nr_interno_conta_w);
            		end if;
            		ds_retorno_w       := nr_interno_conta_w || to_char(nr_seq_protocolo_w);
        	end;
	elsif (ie_forma_w = '21') then --Guia prestador da autorizacao convenio atraves do procedimento ou de mesma guia e senha
		begin
		select	a.cd_autorizacao_prest
		into STRICT	ds_retorno_w
		from	autorizacao_convenio a,
			procedimento_autorizado b,
			estagio_autorizacao c
		where	a.nr_seq_estagio	= c.nr_sequencia
		and	a.nr_sequencia		= b.nr_sequencia_autor
		and	b.nr_sequencia		= nr_seq_proc_autor_w
		and	a.nr_atendimento	= nr_atendimento_w
		and	a.cd_convenio		= cd_convenio_w
		and	coalesce(a.cd_senha,'X')	= coalesce(cd_senha_w,coalesce(a.cd_senha,'X'))  LIMIT 1;
		exception
		when no_data_found then
			begin
			select	a.cd_autorizacao_prest
			into STRICT	ds_retorno_w
			from	autorizacao_convenio a,
				procedimento_autorizado b,
				estagio_autorizacao c
			where	a.nr_seq_estagio	= c.nr_sequencia
			and	a.nr_sequencia		= b.nr_sequencia_autor
			and	b.cd_procedimento	= cd_procedimento_w
			and	coalesce(b.nr_seq_proc_interno, nr_seq_proc_interno_w) = nr_seq_proc_interno_w
			and	a.nr_atendimento	= nr_atendimento_w
			and	a.cd_convenio		= cd_convenio_w
			and	coalesce(a.cd_senha,'X')	= coalesce(cd_senha_w,coalesce(a.cd_senha,'X'))  LIMIT 1;
			exception
			when others then
				begin
				select	a.cd_autorizacao_prest
				into STRICT	ds_retorno_w
				from	autorizacao_convenio a,
					estagio_autorizacao c
				where	a.nr_seq_estagio		= c.nr_sequencia			
				and	a.nr_atendimento		= nr_atendimento_w
				and	a.cd_convenio			= cd_convenio_w
				and	coalesce(a.cd_autorizacao,'X')	= coalesce(cd_autorizacao_w,coalesce(a.cd_autorizacao,'X'))
				and	coalesce(a.cd_senha,'X')		= coalesce(cd_senha_w,coalesce(a.cd_senha,'X'))  LIMIT 1;
				exception
				when others then
					ds_retorno_w	:= null;
				end;
			end;
		end;
	end if;

end if;

return	ds_retorno_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
-- REVOKE ALL ON FUNCTION tiss_obter_guia_prest_proc (nr_sequencia_p bigint, nr_seq_partic_p bigint, ie_honorario_p text) FROM PUBLIC;

