CREATE OR REPLACE FUNCTION tiss_obter_guia_principal (nr_atendimento_p bigint, cd_convenio_p bigint) RETURNS varchar AS $body$
DECLARE


NR_DOC_CONV_PRINCIPAL_w	varchar(255);


BEGIN

begin
select	max(coalesce(a.NR_DOC_CONV_PRINCIPAL, a.NR_DOC_CONVENIO))
into STRICT	NR_DOC_CONV_PRINCIPAL_w
from	atend_categoria_convenio a
where	a.nr_atendimento	= nr_atendimento_p
and	a.cd_convenio		= cd_convenio_p
and	a.dt_inicio_vigencia	=
	(SELECT	min(x.dt_inicio_vigencia)
	 from	atend_categoria_convenio x
	 where	x.nr_atendimento	= a.nr_atendimento
	 and	x.cd_convenio		= a.cd_convenio);
exception
when others then
	NR_DOC_CONV_PRINCIPAL_w	:= '';
end;

return	NR_DOC_CONV_PRINCIPAL_w;

end;
$body$
