SET client_encoding TO 'UTF8';





CREATE OR REPLACE FUNCTION tiss_obter_guia_proc_princ (nr_interno_conta_p bigint, nr_seq_guia_p bigint) RETURNS bigint AS $body$
DECLARE


nr_seq_guia_w		bigint;
nr_seq_procedimento_w	bigint;
count_w			bigint;
ie_tiss_tipo_guia_w	varchar(10);


BEGIN

select	coalesce(max(nr_sequencia),0)
into STRICT	nr_seq_procedimento_w
from	procedimento_paciente
where	nr_interno_conta	= nr_interno_conta_p
and	ie_proc_princ_atend	= 'S';

if (nr_seq_procedimento_w  = 0) then

	select	(obter_proc_principal(	a.nr_atendimento,
						a.cd_convenio_parametro,
						a.ie_tipo_atend_conta,
						a.nr_interno_conta,
						'SEQ'))::numeric
	into STRICT	nr_seq_procedimento_w
	from	conta_paciente a
	where	a.nr_interno_conta	= nr_interno_conta_p;
end if;

if (nr_seq_procedimento_w > 0) then

	select	max(a.nr_seq_guia)
	into STRICT	nr_seq_guia_w
	from	procedimento_paciente b,
		tiss_conta_proc a
	where	a.nr_interno_conta			= b.nr_interno_conta
	and	coalesce(b.cd_motivo_exc_conta::text, '') = ''
	and (somente_numero(a.cd_procedimento)	= b.cd_procedimento or
		somente_numero(a.cd_procedimento)	= b.cd_procedimento_tuss or
		somente_numero(a.cd_procedimento)	= somente_numero(b.cd_procedimento_convenio))
	and	a.nr_interno_conta			= nr_interno_conta_p
	and	b.nr_sequencia				= nr_seq_procedimento_w
	and	a.ie_tiss_tipo_guia			in ('4','5');

	nr_seq_guia_w	:= coalesce(nr_seq_guia_w,nr_seq_guia_p);
else
	nr_seq_guia_w	:= nr_seq_guia_p;
end if;

return 	nr_seq_guia_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
