CREATE OR REPLACE FUNCTION tiss_obter_guia_proc (ie_tipo_atendimento_p bigint, cd_procedimento_p bigint, ie_origem_proced_p bigint, ie_entra_conta_p text, ie_repassa_medico_p text, ie_tiss_tipo_guia_p text) RETURNS varchar AS $body$
DECLARE


ds_retorno_w	varchar(20);


BEGIN

if (ie_tiss_tipo_guia_p IS NOT NULL AND ie_tiss_tipo_guia_p::text <> '') then
	ds_retorno_w		:= ie_tiss_tipo_guia_p;
else
	if (ie_entra_conta_p = 'N') and (ie_repassa_medico_p = 'N')  then					-- Honorário individual
		ds_retorno_w	:= '6';
	elsif (obter_classif_material_proced(null, cd_procedimento_p, ie_origem_proced_p) in (2,3)) then 	-- Outras despesas
		ds_retorno_w	:= '7';
	elsif (ie_tipo_atendimento_p <> 1) then								-- Guia de SPSADT
		ds_retorno_w	:= '4';
	elsif (ie_tipo_atendimento_p = 1) then								-- Guia de Internação
		ds_retorno_w	:= '5';
	end if;

end if;

return	ds_retorno_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
