CREATE OR REPLACE FUNCTION tiss_obter_lado_proc_prescr (nr_seq_procedimento_p bigint, nr_prescricao_p bigint, nr_sequencia_p bigint) RETURNS varchar AS $body$
DECLARE


ie_lado_w	varchar(10);
ds_retorno_w	varchar(255);

BEGIN

begin

select	max(ie_lado)
into STRICT	ie_lado_w
from	procedimento_paciente
where	nr_sequencia	= nr_seq_procedimento_p;

if (coalesce(ie_lado_w::text, '') = '') then
	select	max(ie_lado)
	into STRICT	ie_lado_w
	from	prescr_procedimento
	where	nr_prescricao	= nr_prescricao_p
	and	nr_sequencia	= nr_sequencia_p;
end if;

exception
when others then
	null;
end;

select	substr(obter_valor_dominio(1372, ie_lado_w),1,254)
into STRICT	ds_retorno_w
;

return	ds_retorno_w;

end;
$body$
