CREATE OR REPLACE FUNCTION tiss_obter_lote_envio ( nr_seq_protocolo_p bigint, ie_tiss_tipo_guia_p text) RETURNS bigint AS $body$
DECLARE

 
nr_seq_lote_envio_w	bigint;
nr_lote_sadt_w		bigint;
nr_lote_ri_w		bigint;
nr_lote_honor_w		bigint;
nr_lote_consulta_w		bigint;


BEGIN 
 
if (nr_seq_protocolo_p IS NOT NULL AND nr_seq_protocolo_p::text <> '') then 
	 
	select	max(nr_protocolo_spsadt), 
		max(nr_protocolo_ri), 
		max(nr_protocolo_honor), 
		max(nr_protocolo_cons) 
	into STRICT	nr_lote_sadt_w, 
		nr_lote_ri_w, 
		nr_lote_honor_w, 
		nr_lote_consulta_w 
	from	protocolo_convenio 
	where	nr_seq_protocolo	= nr_seq_protocolo_p;
 
	if (ie_tiss_tipo_guia_p = '3') then --Consulta 
		nr_seq_lote_envio_w	:= nr_lote_consulta_w;
	elsif (ie_tiss_tipo_guia_p = '4') then --SADT 
		nr_seq_lote_envio_w	:= nr_lote_sadt_w;
	elsif (ie_tiss_tipo_guia_p = '5') then -- RI 
		nr_seq_lote_envio_w	:= nr_lote_ri_w;
	elsif (ie_tiss_tipo_guia_p = '6') then --HI 
		nr_seq_lote_envio_w	:= nr_lote_honor_w;
	end if;
 
	if (coalesce(nr_seq_lote_envio_w::text, '') = '') then -- se não tiver pro guia, é o próprio protocolo 
		nr_seq_lote_envio_w	:= nr_seq_protocolo_p;
	end if;	
end if;
 
return	nr_seq_lote_envio_w;
 
end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
