SET client_encoding TO 'UTF8';





CREATE OR REPLACE FUNCTION tiss_obter_lote_recurso_glosa (nr_seq_rec_glosa_prot_p bigint, cd_convenio_p bigint, cd_estabelecimento_p bigint) RETURNS varchar AS $body$
DECLARE


nr_lote_w			varchar(12);
cd_convenio_w			convenio.cd_convenio%type;
cd_estab_w			estabelecimento.cd_estabelecimento%type;
nr_lote_convenio_w		varchar(255);
ie_gerar_lote_rec_tiss_w	tiss_parametros_convenio.ie_gerar_lote_rec_tiss%type;

BEGIN



select	max(a.cd_convenio),
	max(c.nr_lote),
	max(a.cd_estabelecimento)
into STRICT	cd_convenio_w,
	nr_lote_convenio_w,
	cd_estab_w
from	lote_auditoria a,
	lote_audit_hist b,
	tiss_recurso_glosa_prot c
where	a.nr_sequencia = b.nr_seq_lote_audit
and	b.nr_sequencia = c.nr_seq_lote_hist
and	c.nr_sequencia = nr_seq_rec_glosa_prot_p;


select	coalesce(max(ie_gerar_lote_rec_tiss),'L')
into STRICT	ie_gerar_lote_rec_tiss_w
from	tiss_parametros_convenio
where	cd_estabelecimento = cd_estab_w
and	cd_convenio	   = cd_convenio_w;




if (ie_gerar_lote_rec_tiss_w = 'S') then
	nr_lote_w := nr_seq_rec_glosa_prot_p;
else
	nr_lote_w := nr_lote_convenio_w;
end if;



return	nr_lote_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
