CREATE OR REPLACE FUNCTION tiss_obter_mat_atend_pac_valor (nr_seq_Material_p bigint) RETURNS bigint AS $body$
DECLARE


vl_material_w		double precision;


BEGIN

select	coalesce(sum(a.vl_material),0) + coalesce(sum(b.vl_material),0)
into STRICT	vl_material_w
FROM material_atend_paciente b
LEFT OUTER JOIN mat_atend_paciente_valor a ON (b.nr_sequencia = a.nr_seq_material AND 3 = a.ie_tipo_valor)
WHERE b.nr_sequencia		= nr_seq_Material_p;

return	vl_material_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
