CREATE OR REPLACE FUNCTION tiss_obter_med_proc_presc ( nr_prescricao_p bigint, nr_sequencia_prescricao_p bigint) RETURNS varchar AS $body$
DECLARE
	-- Change this to reflect the dblink connection string
	v_conn_str  text := format('port=%s dbname=%s user=%s', current_setting('port'), current_database(), current_user);
	v_query     text;

	v_ret	varchar;
BEGIN
	v_query := 'SELECT * FROM tiss_obter_med_proc_presc_atx ( ' || quote_nullable(nr_prescricao_p) || ',' || quote_nullable(nr_sequencia_prescricao_p) || ' )';
