CREATE OR REPLACE FUNCTION tiss_obter_med_proc_presc_atx ( nr_prescricao_p bigint, nr_sequencia_prescricao_p bigint) RETURNS varchar AS $body$
DECLARE


ds_retorno_w			varchar(255);
cd_medico_solic_proc_prescr_w	varchar(255);
BEGIN

select	max(b.cd_medico_solicitante)
into STRICT	cd_medico_solic_proc_prescr_w
from	prescr_procedimento b
where	b.nr_prescricao	= nr_prescricao_p
and	b.nr_sequencia	= nr_sequencia_prescricao_p;

if (coalesce(cd_medico_solic_proc_prescr_w,'X') = 'X') then
	select	max(b.cd_medico)
	into STRICT	cd_medico_solic_proc_prescr_w
	from	prescr_medica b
	where	b.nr_prescricao	= nr_prescricao_p;
end if;

ds_retorno_w	 := cd_medico_solic_proc_prescr_w;

return		ds_retorno_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
