SET client_encoding TO 'UTF8';





CREATE OR REPLACE FUNCTION tiss_obter_motivo_glosa_movto ( nr_seq_ret_glosa_p bigint, cd_motivo_glosa_p bigint, cd_convenio_p bigint) RETURNS varchar AS $body$
DECLARE


cd_motivo_glosa_tiss_w	varchar(10);



BEGIN

if (coalesce(nr_seq_ret_glosa_p,0) > 0) then

	begin
		select	a.cd_motivo_glosa_tiss
		into STRICT	cd_motivo_glosa_tiss_w
		from	convenio_retorno_movto a
		where	a.nr_seq_ret_glosa = nr_seq_ret_glosa_p  LIMIT 1;
	exception
	when others then
		cd_motivo_glosa_tiss_w	:= null;
	end;
	--Se não tem no movimento, então considera do cadastro do motivo de glosa
end if;

if (coalesce(cd_motivo_glosa_tiss_w::text, '') = '') then

	select	max(cd_motivo_tiss)
	into STRICT	cd_motivo_glosa_tiss_w
	from	tiss_motivo_glosa
	where	cd_motivo_glosa	= cd_motivo_glosa_p
	and	coalesce(cd_convenio,cd_convenio_p)	= cd_convenio_p;
end if;

return cd_motivo_glosa_tiss_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
