CREATE OR REPLACE FUNCTION tiss_obter_motivo_glosa (nr_seq_motivo_glosa_p bigint, ie_opcao_p text) RETURNS varchar AS $body$
DECLARE


/*	C - Código glosa
	D - Descrição glosa
	G - Grupo glosa
	DG - Descrição do grupo glosa	*/
ie_grupo_glosa_w		varchar(2);
cd_motivo_tiss_w		varchar(10);
ds_motivo_tiss_w		varchar(255);
ds_grupo_glosa_w		varchar(255);
ds_retorno_w			varchar(255);


BEGIN

select	ie_grupo_glosa,
	cd_motivo_tiss,
	ds_motivo_tiss,
	substr(obter_valor_dominio(1771, ie_grupo_glosa),1,255)
into STRICT	ie_grupo_glosa_w,
	cd_motivo_tiss_w,
	ds_motivo_tiss_w,
	ds_grupo_glosa_w
from	tiss_motivo_glosa
where	nr_sequencia	= nr_seq_motivo_glosa_p;

if (ie_opcao_p = 'C') then
	ds_retorno_w	:= cd_motivo_tiss_w;
elsif (ie_opcao_p = 'D') then
	ds_retorno_w	:= ds_motivo_tiss_w;
elsif (ie_opcao_p = 'G') then
	ds_retorno_w	:= ie_grupo_glosa_w;
elsif (ie_opcao_p = 'DG') then
	ds_retorno_w	:= ds_grupo_glosa_w;
end if;

return ds_retorno_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
