CREATE OR REPLACE FUNCTION tiss_obter_obs_prescr_proc (nr_prescricao_p bigint, cd_procedimento_p bigint, ie_origem_proced_p bigint) RETURNS varchar AS $body$
DECLARE


ds_observacao_w		varchar(4000);
ds_retorno_w		varchar(4000);

c01 CURSOR FOR
SELECT	distinct ds_observacao
from	prescr_procedimento
where	nr_prescricao		= nr_prescricao_p
and	cd_procedimento		= cd_procedimento_p
and	ie_origem_proced	= ie_origem_proced_p;


BEGIN

ds_retorno_w	:= null;
ds_observacao_w	:= null;

open C01;
loop
fetch C01 into
	ds_observacao_w;
EXIT WHEN NOT FOUND; /* apply on C01 */

	if (ds_observacao_w IS NOT NULL AND ds_observacao_w::text <> '') then
		if (coalesce(ds_retorno_w::text, '') = '') then
			ds_retorno_w	:= substr(ds_observacao_w,1,3999);
		else
			ds_retorno_w	:= substr((ds_retorno_w||' - '||ds_observacao_w),1,3999);
		end if;
	end if;

end loop;
close C01;

return 	ds_retorno_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
