CREATE OR REPLACE FUNCTION tiss_obter_obs_solic_spsadt (cd_convenio_p bigint, cd_estabelecimento_p bigint, nm_usuario_p text) RETURNS varchar AS $body$
DECLARE


ds_observacao_w	varchar(255) := null;
ie_observacao_w	varchar(1);


BEGIN

select	coalesce(max(a.ie_observacao),'N')
into STRICT	ie_observacao_w
from	tiss_parametros_convenio a
where	a.cd_convenio		= cd_convenio_p
and	a.cd_estabelecimento	= cd_estabelecimento_p;

if (ie_observacao_w = 'U') then
	ds_observacao_w	:= nm_usuario_p;
end if;

return	ds_observacao_w;

end	;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
