CREATE OR REPLACE FUNCTION tiss_obter_origem_preco_mat (ie_origem_preco_p text, cd_material_p bigint, cd_estabelecimento_p bigint, cd_convenio_p bigint, cd_categoria_p text, cd_tab_preco_material_p bigint, dt_vigencia_p timestamp, cd_setor_atendimento_p bigint, ie_opcao_p text, nr_seq_proc_pacote_p bigint, nr_seq_mat_paciente_p bigint, cd_material_tuss_p text default null) RETURNS varchar AS $body$
DECLARE


/*
ie_opcao_p	
'X' - codigo para XML
'R' - codigo para relatorio
*/
ie_tipo_material_w			varchar(10);
ds_retorno_w			varchar(10) := '00';
NR_SEQ_TISS_TABELA_w		bigint;
cd_tabela_relat_w			varchar(10);
cd_tabela_xml_w			varchar(10);
cd_grupo_material_w		bigint;
cd_subgrupo_material_w		bigint;
cd_classe_material_w		bigint;
ie_origem_preco_mat_w		bigint;
vl_unitario_mat_w		double precision := 0;
nr_seq_familia_w		material.nr_seq_familia%type;

c01 CURSOR FOR
SELECT	a.nr_seq_tiss_tabela
from 	tiss_regra_tabela_mat a
where	a.cd_estabelecimento						= cd_estabelecimento_p
and	a.cd_convenio							= cd_convenio_p
and	coalesce(a.cd_categoria, cd_categoria_p)				= cd_categoria_p
and	coalesce(a.cd_material, cd_material_p)				= cd_material_p
and	coalesce(a.cd_grupo_material, cd_grupo_material_w)			= cd_grupo_material_w
and	coalesce(a.cd_subgrupo_material, cd_subgrupo_material_w)		= cd_subgrupo_material_w
and	coalesce(a.cd_classe_material, cd_classe_material_w)			= cd_classe_material_w
and	coalesce(dt_vigencia_p, clock_timestamp())					between trunc(a.dt_inicio_vigencia, 'dd') and ESTABLISHMENT_TIMEZONE_UTILS.endOfDay(coalesce(a.dt_final_vigencia,coalesce(dt_vigencia_p, clock_timestamp())+1))
and	coalesce(a.ie_situacao,'A') = 'A'
and (coalesce(a.cd_tab_preco_material, coalesce(cd_tab_preco_material_p, '00')) = coalesce(cd_tab_preco_material_p, '00'))
and	coalesce(a.cd_setor_atendimento,coalesce(cd_setor_atendimento_p,0))		= coalesce(cd_setor_atendimento_p,0)
and	coalesce(a.ie_origem_preco, coalesce(ie_origem_preco_mat_w,0))		= coalesce(ie_origem_preco_mat_w,0)
and	coalesce(a.nr_seq_familia, coalesce(nr_seq_familia_w,0))			= coalesce(nr_seq_familia_w,0)
and	((coalesce(ie_pacote, 'T') = 'T') or	 
	 ((coalesce(ie_pacote, 'T') = 'I') and (nr_seq_proc_pacote_p IS NOT NULL AND nr_seq_proc_pacote_p::text <> '')) or
	 ((coalesce(ie_pacote, 'T') = 'F') and (coalesce(nr_seq_proc_pacote_p::text, '') = '')))
and	((coalesce(vl_unitario_mat_w,0) >= coalesce(a.vl_inicial,0)) and (coalesce(vl_unitario_mat_w,0) <= coalesce(a.vl_final,999999999)))
and 	coalesce(a.ie_tipo_material, coalesce(ie_tipo_material_w, '0')) = coalesce(ie_tipo_material_w, '0')
and	((coalesce(a.ie_material_tuss,'A') = 'A') or (coalesce(a.ie_material_tuss,'A') = 'S' and coalesce(cd_material_tuss_p,'0') <> '0') or (coalesce(a.ie_material_tuss,'A') = 'N' and coalesce(cd_material_tuss_p,'0') = '0'))
order	by ie_prioridade desc,
	coalesce(cd_material, 0),
	coalesce(cd_classe_material, 0),
	coalesce(cd_subgrupo_material, 0),
	coalesce(cd_grupo_material, 0),
	coalesce(cd_tab_preco_material,0),
	coalesce(a.cd_setor_atendimento,0),
	coalesce(a.ie_origem_preco,0),
	coalesce(a.vl_inicial,0),
	a.dt_inicio_vigencia,
	coalesce(a.nr_seq_familia,0),
	coalesce(a.ie_tipo_material,'0');


BEGIN



select 	b.cd_grupo_material,
	b.cd_subgrupo_material,
	b.cd_classe_material,
	a.ie_tipo_material	
into STRICT	cd_grupo_material_w,
	cd_subgrupo_material_w,
	cd_classe_material_w,
	ie_tipo_material_w	
from	estrutura_material_v b,
	material a
where 	a.cd_material		= b.cd_material
and	a.cd_material		= cd_material_p;

if (ie_origem_preco_p not in (1,4)) then
	ie_origem_preco_mat_w	:= 2;			/*2- Tabela de preco*/
			
else
	ie_origem_preco_mat_w	:= (ie_origem_preco_p)::numeric; 	/*1- Brasindice, 4-Simpro*/
end if;

if (coalesce(nr_seq_mat_paciente_p,0) > 0) then

	select	coalesce(sum(a.vl_unitario),0),
		max(b.nr_seq_familia)
	into STRICT	vl_unitario_mat_w,
		nr_seq_familia_w
	from	material_atend_paciente a,
		material b
	where	a.cd_material	= b.cd_material
	and	a.nr_sequencia 	= nr_seq_mat_paciente_p;
	
end if;


nr_seq_tiss_tabela_w	:= null;
open c01;
loop
fetch c01 into
	nr_seq_tiss_tabela_w;
EXIT WHEN NOT FOUND; /* apply on c01 */
end loop;
close c01;

if (coalesce(nr_seq_tiss_tabela_w,0) > 0) then

	select	cd_tabela_relat,
		cd_tabela_xml
	into STRICT	cd_tabela_relat_w,
		cd_tabela_xml_w
	from	tiss_tipo_tabela
	where	nr_sequencia	= NR_SEQ_TISS_TABELA_w;

	if (ie_opcao_p = 'X') then
		ds_retorno_w	:= cd_tabela_xml_w;
	elsif (ie_opcao_p = 'R') then
		ds_retorno_w	:= cd_tabela_relat_w;
	end if;

else
	if (ie_tipo_material_w = 1) then		/* Materiais */
		if (ie_origem_preco_p = 1) then	/* Brasindice */
			ds_retorno_w	:= '05';
		elsif (ie_origem_preco_p = 4) then	/* Simpro */
			ds_retorno_w	:= '12';
		elsif (ie_origem_preco_p = 2) then	/* Tabela preco */
			ds_retorno_w	:= '95';
		end if;

	elsif (ie_tipo_material_w in (2,3)) then	/* Medicamentos */
		if (ie_origem_preco_p = 1) then	/* Brasindice */
			ds_retorno_w	:= '05';
		elsif (ie_origem_preco_p = 4) then	/* Simpro */
			ds_retorno_w	:= '12';
		elsif (ie_origem_preco_p = 2) then	/* Tabela preco */
			ds_retorno_w	:= '96';
		end if;
		
	elsif (ie_tipo_material_w = 5) then	/* Generos Alimenticios */
	
		if (ie_origem_preco_p = 1) then	/* Brasindice */
			ds_retorno_w	:= '05';
		elsif (ie_origem_preco_p = 4) then	/* Simpro */
			ds_retorno_w	:= '12';
		elsif (ie_origem_preco_p = 2) then	/* Tabela preco */
			ds_retorno_w	:= '95';
		end if;

	end if;
end if;

return	ds_retorno_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
-- REVOKE ALL ON FUNCTION tiss_obter_origem_preco_mat (ie_origem_preco_p text, cd_material_p bigint, cd_estabelecimento_p bigint, cd_convenio_p bigint, cd_categoria_p text, cd_tab_preco_material_p bigint, dt_vigencia_p timestamp, cd_setor_atendimento_p bigint, ie_opcao_p text, nr_seq_proc_pacote_p bigint, nr_seq_mat_paciente_p bigint, cd_material_tuss_p text default null) FROM PUBLIC;

