SET client_encoding TO 'UTF8';





CREATE OR REPLACE FUNCTION tiss_obter_proc_princ (nr_interno_conta_p bigint, nr_seq_conta_proc_p bigint) RETURNS varchar AS $body$
DECLARE


nr_seq_proc_w		bigint;
cd_procedimento_w	varchar(20);
ie_proc_princ_atend_w	varchar(255);


BEGIN

ie_proc_princ_atend_w	:= null;

if (nr_seq_conta_proc_p IS NOT NULL AND nr_seq_conta_proc_p::text <> '') then

	select	max(nr_seq_proc),
		max(cd_procedimento)
	into STRICT	nr_seq_proc_w,
		cd_procedimento_w
	from	tiss_conta_proc
	where	nr_sequencia 	= nr_seq_conta_proc_p
	and	ie_tiss_tipo_guia in ('4','5');

	if (nr_seq_proc_w IS NOT NULL AND nr_seq_proc_w::text <> '') then

		begin
		select	ie_proc_princ_atend
		into STRICT	ie_proc_princ_atend_w
		from	procedimento_paciente
		where	nr_sequencia	= nr_seq_proc_w;
		exception
		when others then
			ie_proc_princ_atend_w	:= null;
		end;
	elsif (cd_procedimento_w IS NOT NULL AND cd_procedimento_w::text <> '') then
		begin
		select	b.ie_proc_princ_atend
		into STRICT	ie_proc_princ_atend_w
		from	procedimento_paciente b,
			tiss_conta_proc a
		where	a.nr_interno_conta			= b.nr_interno_conta
		and	coalesce(b.cd_motivo_exc_conta::text, '') = ''
		and ((a.cd_procedimento)::numeric 		= b.cd_procedimento or
			(a.cd_procedimento)::numeric 		= b.cd_procedimento_tuss or
			somente_numero(a.cd_procedimento)	= somente_numero(b.cd_procedimento_convenio))
		and	a.nr_interno_conta			= nr_interno_conta_p
		and	a.nr_sequencia				= nr_seq_conta_proc_p;
		exception
		when others then
			ie_proc_princ_atend_w	:= null;
		end;

	end if;

end if;

return ie_proc_princ_atend_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
