SET client_encoding TO 'UTF8';





CREATE OR REPLACE FUNCTION tiss_obter_proc_tuss_autor ( cd_estabelecimento_p bigint, cd_convenio_p bigint, nr_seq_procedimento_p bigint, ie_opcao_p text) RETURNS varchar AS $body$
DECLARE

/*
ie_opcao_p
'C' - código
'D' - Descrição
*/
ie_desc_proc_interno_w		varchar(2);
ie_proc_tuss_w			varchar(2);
cd_procedimento_tuss_w		varchar(20);
ds_procedimento_tuss_w		varchar(255);
ds_retorno_w			varchar(255);
cd_procedimento_convenio_w	varchar(20);
cd_procedimento_w		varchar(20);
cd_proc_autor_w			bigint;
ie_origem_proc_w		varchar(1);
nr_seq_proc_interno_w		bigint;
nr_seq_exame_w			bigint;
ie_conversao_sadt_solic_w	varchar(2) := 'S';


BEGIN
ds_retorno_w	:= null;

if (cd_estabelecimento_p IS NOT NULL AND cd_estabelecimento_p::text <> '') and (cd_convenio_p IS NOT NULL AND cd_convenio_p::text <> '') then
	select	coalesce(max(IE_DESC_PROC_INTERNO), 'N'),
		coalesce(max(ie_proc_tuss),'N'),
		coalesce(max(ie_conversao_sadt_solic),'S')
	into STRICT	ie_desc_proc_interno_w,
		ie_proc_tuss_w,
		ie_conversao_sadt_solic_w
	from	tiss_parametros_convenio
	where	cd_convenio		= cd_convenio_p
	and	cd_estabelecimento	= cd_estabelecimento_p;
end if;

if (nr_seq_procedimento_p IS NOT NULL AND nr_seq_procedimento_p::text <> '') then
	select 	to_char(cd_procedimento_tuss),
		cd_procedimento_convenio,
		cd_procedimento,
		ie_origem_proced,
		nr_seq_proc_interno,
		nr_seq_exame,
		ds_proc_convenio
	into STRICT 	cd_procedimento_tuss_w,
		cd_procedimento_convenio_w,
		cd_proc_autor_w,
		ie_origem_proc_w,
		nr_seq_proc_interno_w,
		nr_seq_exame_w,
		ds_procedimento_tuss_w
	from 	procedimento_autorizado
	where 	nr_sequencia 	= nr_seq_procedimento_p;

	if (ie_desc_proc_interno_w = 'T') and (coalesce(cd_procedimento_tuss_w,'0') <> '0') then --procedimento  tuss
		ds_procedimento_tuss_w	:= obter_descricao_procedimento(cd_procedimento_tuss_w,8);
	elsif (ie_desc_proc_interno_w = 'P') then --procedimento
		ds_procedimento_tuss_w 	:= obter_descricao_procedimento(cd_proc_autor_w, ie_origem_proc_w);
	elsif (ie_desc_proc_interno_w = 'N') then --exame ou procedimento
		ds_procedimento_tuss_w 	:= obter_desc_prescr_proc_exam(cd_proc_autor_w, ie_origem_proc_w, nr_seq_proc_interno_w, nr_seq_exame_w);
	elsif (ie_desc_proc_interno_w = 'S') then --proc interno ou procedimento
		ds_procedimento_tuss_w 	:= obter_desc_prescr_proc(cd_proc_autor_w, ie_origem_proc_w, nr_seq_proc_interno_w);
	elsif (ie_desc_proc_interno_w = 'C') and (coalesce(coalesce(cd_procedimento_convenio_w, cd_procedimento_tuss_w),'0') <> '0') then --conversão ou tuss
		select	coalesce(	ds_procedimento_tuss_w,
				CASE WHEN coalesce(cd_procedimento_tuss_w,'0')='0' THEN 					obter_descricao_procedimento(cd_proc_autor_w, ie_origem_proc_w)  ELSE obter_descricao_procedimento(cd_procedimento_tuss_w, 8) END )
		into STRICT	ds_procedimento_tuss_w
		;
	end if;

	if (ie_proc_tuss_w	= 'S') and (coalesce(cd_procedimento_tuss_w,'0') <> '0') then
		cd_procedimento_w	:= cd_procedimento_tuss_w;
	elsif (ie_proc_tuss_w	= 'C') then
		if (ie_conversao_sadt_solic_w  = 'N') then
			cd_procedimento_w	:= cd_procedimento_tuss_w;
		else
			cd_procedimento_w	:= coalesce(cd_procedimento_convenio_w,cd_procedimento_tuss_w);
		end if;
	end if;

	if (coalesce(cd_procedimento_w,'0') = '0') then
		cd_procedimento_w	:= to_char(cd_proc_autor_w);
	end if;

	if (ie_opcao_p = 'C') then
		ds_retorno_w	:= cd_procedimento_w;
	elsif (ie_opcao_p = 'D') then
		select	substr(
			tiss_eliminar_caractere(
				coalesce(ds_procedimento_tuss_w,
					CASE WHEN 	coalesce(cd_procedimento_convenio_w::text, '') = '' THEN 						coalesce(obter_desc_prescr_proc_exam(cd_proc_autor_w, ie_origem_proc_w, nr_seq_proc_interno_w, nr_seq_exame_w),							obter_descricao_procedimento(cd_proc_autor_w, ie_origem_proc_w))  ELSE tiss_obter_desc_conversao_proc(cd_convenio_p, cd_proc_autor_w, ie_origem_proc_w, nr_seq_proc_interno_w, cd_procedimento_convenio_w) END
				)
			),1,150)
		into STRICT	ds_retorno_w
		;
	end if;

end if;

return	ds_retorno_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
