SET client_encoding TO 'UTF8';





CREATE OR REPLACE FUNCTION tiss_obter_prot_solic_status ( nr_seq_protocolo_p bigint, nr_seq_protocolo_receb_p bigint default 0) RETURNS varchar AS $body$
DECLARE

 
cd_convenio_w		bigint;
cd_estabelecimento_w	bigint;
ie_prot_solic_status_w	varchar(255);
nr_protocolo_receb_w	varchar(255);


BEGIN 
 
select	max(a.cd_convenio), 
	max(a.cd_estabelecimento) 
into STRICT	cd_convenio_w, 
	cd_estabelecimento_w 
from	protocolo_convenio a 
where	a.nr_seq_protocolo	= nr_seq_protocolo_p;
 
select	max(coalesce(ie_prot_solic_status,'P')) 
into STRICT	ie_prot_solic_status_w 
from	tiss_parametros_convenio 
where	cd_convenio		= cd_convenio_w 
and	cd_estabelecimento	= cd_estabelecimento_w;
 
nr_protocolo_receb_w	:= null;
 
 
if (ie_prot_solic_status_w = 'R') then 
 
	if (nr_seq_protocolo_receb_p > 0) then 
	 
		select	max(nr_protocolo_receb) 
		into STRICT	nr_protocolo_receb_w 
		from	tiss_protocolo_envio_ret 
		where	nr_seq_protocolo	= nr_seq_protocolo_p 
		and	nr_sequencia		= nr_seq_protocolo_receb_p;
	else 
		select	max(nr_protocolo_receb) 
		into STRICT	nr_protocolo_receb_w 
		from	tiss_protocolo_envio_ret 
		where	nr_seq_protocolo	= nr_seq_protocolo_p;
	end if;
		 
	 
		 
	if (coalesce(nr_protocolo_receb_w::text, '') = '') then 
		select	max(nr_lote) 
		into STRICT	nr_protocolo_receb_w 
		from	tiss_situacao_protocolo 
		where	nr_seq_protocolo	= nr_seq_protocolo_p;
	end if;
end if;
 
return	nr_protocolo_receb_w;
 
end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
