CREATE OR REPLACE FUNCTION tiss_obter_qtdade_guia_prescr (cd_estabelecimento_p bigint, cd_convenio_p bigint, cd_procedimento_p bigint, ie_origem_proced_p bigint) RETURNS bigint AS $body$
DECLARE


qt_vias_w		smallint;
cd_tipo_procedimento_w	bigint;

c01 CURSOR FOR
SELECT	qt_vias
from	tiss_regra_qtd_guia_prescr
where	cd_estabelecimento						= cd_estabelecimento_p
and	coalesce(cd_convenio, coalesce(cd_convenio_p,0))				= coalesce(cd_convenio_p,0)
and	coalesce(cd_tipo_procedimento, coalesce(cd_tipo_procedimento_w,0))	= coalesce(cd_tipo_procedimento_w,0)
and	ie_situacao	= 'A'
order by coalesce(cd_convenio,0),
	coalesce(cd_tipo_procedimento,0);


BEGIN

select	max(cd_tipo_procedimento)
into STRICT	cd_tipo_procedimento_w
from	procedimento
where	cd_procedimento		= cd_procedimento_p
and	ie_origem_proced	= ie_origem_proced_p;

open C01;
loop
fetch C01 into
	qt_vias_w;
EXIT WHEN NOT FOUND; /* apply on C01 */
end loop;
close C01;

return	qt_vias_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
