CREATE OR REPLACE FUNCTION tiss_obter_qtd_glosa_prot_rec (nr_seq_prot_receb_p bigint) RETURNS varchar AS $body$
DECLARE


qt_glosa_protocolo_w	bigint;


BEGIN
	begin
        select	count(DS_TIPO_GLOSA) --as 'Glosas de protocolo'
        into STRICT	qt_glosa_protocolo_w
        from	tiss_situacao_prot_glosa a
        where 	a.nr_seq_protoc_envio_ret	= nr_seq_prot_receb_p
        and     a.ds_tipo_glosa = 'Glosas do Protocolo'
;

        exception
            when others then
            qt_glosa_protocolo_w	:= 0;
        end;

return	qt_glosa_protocolo_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
