SET client_encoding TO 'UTF8';





CREATE OR REPLACE FUNCTION tiss_obter_qtd_mat (nr_sequencia_p bigint, ie_qtd_matereial_p text) RETURNS bigint AS $body$
DECLARE


/* ie_qtd_matereial_p
M	QT_MATERIAL
E	QT_ESTOQUE
*/
qt_material_w		double precision;
qt_estoque_w		double precision;
qt_retorno_w		double precision;
nr_atendimento_w		bigint;
nr_seq_mat_glosa_w	bigint;


BEGIN

begin
select	qt_material,
	qt_estoque,
	nr_atendimento,
	nr_seq_mat_glosa
into STRICT	qt_material_w,
	qt_estoque_w,
	nr_atendimento_w,
	nr_seq_mat_glosa_w
from	material_atend_paciente
where	nr_sequencia	= nr_sequencia_p;
exception
when others then
	qt_material_w	:= 0;
	qt_estoque_w	:= 0;
end;

if (ie_qtd_matereial_p = 'M') then
	qt_retorno_w	:= qt_material_w;
elsif (ie_qtd_matereial_p = 'E') then
	qt_retorno_w	:= coalesce(qt_estoque_w,qt_material_w);
elsif (ie_qtd_matereial_p = 'G') then
	select	coalesce(max(Obter_Qt_ProcMat_Glosa(nr_seq_mat_glosa_w,'M',nr_atendimento_w)),0)
	into STRICT	qt_retorno_w
	;

	if (qt_retorno_w = 0) then
		qt_retorno_w	:= qt_material_w;
	end if;
end if;

return	qt_retorno_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
