CREATE OR REPLACE FUNCTION tiss_obter_qtd_proc_prescr (cd_estabelecimento_p bigint, cd_convenio_p bigint, nr_prescricao_p bigint, nr_seq_proc_prescr_p bigint) RETURNS bigint AS $body$
DECLARE


ie_qt_proc_prescr_interv_w	tiss_parametros_convenio.ie_qt_proc_prescr_interv%type;
ie_quebra_horarios_prescr_w	tiss_parametros_convenio.ie_quebra_horarios_prescr%type;
ds_horarios_w			prescr_procedimento.ds_horarios%type;
qt_procedimento_w		prescr_procedimento.qt_procedimento%type;
qt_retorno_w			prescr_procedimento.qt_procedimento%type;


BEGIN

select	coalesce(max(ie_qt_proc_prescr_interv),'N'),
	coalesce(max(ie_quebra_horarios_prescr),'N')
into STRICT	ie_qt_proc_prescr_interv_w,
	ie_quebra_horarios_prescr_w
from	tiss_parametros_convenio
where	cd_convenio		= cd_convenio_p
and	cd_estabelecimento	= cd_estabelecimento_p;

select	max(ds_horarios),
	max(qt_procedimento)
into STRICT	ds_horarios_w,
	qt_procedimento_w
from	prescr_procedimento
where	nr_prescricao	= nr_prescricao_p
and	nr_sequencia	= nr_seq_proc_prescr_p;

--Se está parametrizado para quebrar por horário, então nem considera o parâmetro da quantidade, pois irá gerar uma guia para cada horário e iria duplicar/triplicar as quantidades de acordo com o horário
if (ie_quebra_horarios_prescr_w = 'S') then
	qt_retorno_w	:= qt_procedimento_w;
elsif (ie_qt_proc_prescr_interv_w = 'S') then
	select	coalesce(max(obter_quantidade_intervalos(ds_horarios_w)),qt_procedimento_w)
	into STRICT	qt_retorno_w
	;
else
	qt_retorno_w	:= qt_procedimento_w;
end if;

return 	qt_retorno_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
