CREATE OR REPLACE FUNCTION tiss_obter_qt_dias_ciclo_atual (nr_seq_paciente_setor_p bigint, nr_ciclo_p bigint) RETURNS bigint AS $body$
DECLARE

qt_dia_ciclo_w	bigint;

/*
NR_SEQ_PACIENTE_SETOR_P = AUTORIZACAO_CONVENIO.NR_SEQ_PACIENTE_SETOR
NR_CICLO		              = AUTORIZACAO_CONVENIO.NR_CICLO
*/
BEGIN

select	count(*)
into STRICT	qt_dia_ciclo_w
from	paciente_atendimento
where	nr_seq_paciente = nr_seq_paciente_setor_p
and	nr_ciclo	= nr_ciclo_p;

return	qt_dia_ciclo_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
