CREATE OR REPLACE FUNCTION tiss_obter_qt_proc_pacote (nr_interno_conta_p bigint, cd_procedimento_p bigint) RETURNS bigint AS $body$
DECLARE


qt_procedimento_w	double precision;
count_pacote_w		bigint;


BEGIN

select	count(*)
into STRICT	count_pacote_w
from	procedimento_paciente
where	nr_interno_conta	= nr_interno_conta_p
and	nr_sequencia		= nr_seq_proc_pacote
and	cd_procedimento		= cd_procedimento_p
and	coalesce(cd_motivo_exc_conta::text, '') = '';

if (count_pacote_w > 1) then
	qt_procedimento_w	:= 1;
else
	select	sum(qt_procedimento)
	into STRICT	qt_procedimento_w
	from	procedimento_paciente
	where	nr_interno_conta	= nr_interno_conta_p
	and	cd_procedimento		= cd_procedimento_p
	and	coalesce(cd_motivo_exc_conta::text, '') = ''
	and	(nr_seq_proc_pacote IS NOT NULL AND nr_seq_proc_pacote::text <> '')
	and	nr_seq_proc_pacote	<> nr_sequencia;
end if;

return	qt_procedimento_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
