CREATE OR REPLACE FUNCTION tiss_obter_regra_campo (ie_tipo_guia_p text, nm_atributo_p text, cd_convenio_p text, vl_campo_p text, ie_tipo_atendimento_p bigint, cd_categoria_conv_p text, ie_tipo_despesa_p text, vl_max_campo_p bigint, cd_estabelecimento_p bigint, ie_clinica_p bigint, nr_seq_classificacao_p bigint, cd_setor_atendimento_p bigint, ds_parametros_p text, cd_setor_execucao_p bigint default null) RETURNS varchar AS $body$
DECLARE


/*
ESTA FUNCTION E UTILIZADA TAMBEM NOS PROJETOS XML (SOLICITACAO PROCEDIMENTO -> DIAGNOSTICO)
*/
ie_envio_w			varchar(4000);
ds_padrao_w			varchar(4000);
vl_permitido_w			double precision;
ds_retorno_w			varchar(4000);
ds_temp_w			varchar(4000);
dt_retorno_aux_w		timestamp;
ds_valor_gerado_w		varchar(4000);
ds_conteudo_w			varchar(4000);
vl_atributo_w			varchar(255);
ie_tipo_atend_tiss_w		varchar(255);
ie_tipo_atend_tiss_proc_w	varchar(255);
ds_mascara_w			varchar(255);
qt_digitos_mascara_w		bigint;
qt_digitos_valor_w		bigint;
qt_registros_vetor_w		bigint;
k				integer;
cd_perfil_w			integer := wheb_usuario_pck.get_cd_perfil;

ds_versao_tiss_w		varchar(20);
nm_atributo_w			varchar(255);
qt_regra_w			bigint;
qt_existentes_w			integer := 0;
qt_geradas_w			integer := 0;
cd_convenio_w			convenio.cd_convenio%type := (cd_convenio_p)::numeric;
c01 CURSOR FOR
SELECT	coalesce(a.ie_envio, 'E'),
	a.ds_padrao,
	a.vl_max_campo,
	a.ds_valor_gerado,
	a.ds_mascara
from	tiss_lista_campo b,
	tiss_regra_campo_conv a
where	a.nr_seq_campo		= b.nr_sequencia
and	a.cd_convenio		= cd_convenio_p
and	a.cd_estabelecimento	= cd_estabelecimento_p
and	b.ie_tiss_tipo_guia	= ie_tipo_guia_p
and	b.nm_atributo		= nm_atributo_w
and	((a.ie_taxas = 'N') or (a.ie_taxas = 'S' and ie_tipo_despesa_p in ('4','5'))) 		-- Edgar 29/09/2007, Tratar se a regra acima deve ser aplicada para as taxas
and	((a.ie_gases = 'S') or
	(a.ie_gases = 'N' AND ie_tipo_despesa_p <> '1'))
and	((a.ie_medicamento = 'S') or
	(a.ie_medicamento = 'N' AND ie_tipo_despesa_p <> '2'))
and	((a.ie_material = 'S') or
	(a.ie_material = 'N' AND ie_tipo_despesa_p <> '3'))
and	((a.ie_taxas_diversas = 'S') or
	(a.ie_taxas_diversas = 'N' AND ie_tipo_despesa_p <> '4'))
and	((a.ie_diarias = 'S') or
	(a.ie_diarias = 'N' AND ie_tipo_despesa_p <> '5'))
and	((a.ie_alugueis = 'S') or
	(a.ie_alugueis = 'N' AND ie_tipo_despesa_p <> '6'))
and	coalesce(a.ie_tipo_atendimento, coalesce(ie_tipo_atendimento_p, 0))		= coalesce(ie_tipo_atendimento_p, 0)
and	coalesce(a.ie_clinica, coalesce(ie_clinica_p, 0))					= coalesce(ie_clinica_p, 0)
and	coalesce(a.cd_categoria, coalesce(cd_categoria_conv_p, '0'))			= coalesce(cd_categoria_conv_p, '0')
and	coalesce(a.nr_seq_classificacao, coalesce(nr_seq_classificacao_p, '0'))		= coalesce(nr_seq_classificacao_p, '0')
and	coalesce(a.cd_setor_entrada, coalesce(cd_setor_atendimento_p, '0'))		= coalesce(cd_setor_atendimento_p, '0')
and	coalesce(a.cd_setor_exec, coalesce(cd_setor_execucao_p, '0'))			= coalesce(cd_setor_execucao_p, '0')
and	coalesce(a.ie_tipo_atend_tiss, coalesce(ie_tipo_atend_tiss_w,'0'))		= coalesce(ie_tipo_atend_tiss_w,'0')
and	coalesce(a.ie_tipo_atend_proc_tiss, coalesce(ie_tipo_atend_tiss_proc_w,'0'))	= coalesce(ie_tipo_atend_tiss_proc_w,'0')
and	coalesce(a.cd_perfil,coalesce(cd_perfil_w,0))					= coalesce(cd_perfil_w,0)
order by	a.ie_taxas,
	coalesce(cd_setor_exec, 0),
	coalesce(ie_tipo_atendimento, 0),
	coalesce(ie_clinica_p, 0),
	coalesce(cd_categoria, '0'),
	coalesce(cd_setor_entrada, '0'),
	coalesce(a.nr_seq_classificacao,0),
	coalesce(a.ie_tipo_atend_tiss,'0'),
	coalesce(a.ie_tipo_atend_proc_tiss,'0'),
	coalesce(a.cd_perfil,0);

--Este cursor e  para os casos do TISS 3.2 onde o codigo das despesas e diferente	
c02 CURSOR FOR
SELECT	coalesce(a.ie_envio, 'E'),
	a.ds_padrao,
	a.vl_max_campo,
	a.ds_valor_gerado,
	a.ds_mascara
from	tiss_lista_campo b,
	tiss_regra_campo_conv a
where	a.nr_seq_campo		= b.nr_sequencia
and	a.cd_convenio		= cd_convenio_p
and	a.cd_estabelecimento	= cd_estabelecimento_p
and	b.ie_tiss_tipo_guia	= ie_tipo_guia_p
and	b.nm_atributo		= nm_atributo_w
and	((a.ie_taxas = 'N') or (a.ie_taxas = 'S' and ie_tipo_despesa_p in ('5','7'))) 		-- Edgar 29/09/2007, Tratar se a regra acima deve ser aplicada para as taxas
and	((a.ie_gases = 'S') or
	(a.ie_gases = 'N' AND ie_tipo_despesa_p <> '1'))
and	((a.ie_medicamento = 'S') or
	(a.ie_medicamento = 'N' AND ie_tipo_despesa_p <> '2'))
and	((a.ie_material = 'S') or
	(a.ie_material = 'N' AND ie_tipo_despesa_p <> '3'))
and	((a.ie_taxas_diversas = 'S') or
	(a.ie_taxas_diversas = 'N' AND ie_tipo_despesa_p <> '7'))
and	((a.ie_diarias = 'S') or
	(a.ie_diarias = 'N' AND ie_tipo_despesa_p <> '5'))
and	((a.ie_alugueis = 'S') or
	(a.ie_alugueis = 'N' AND ie_tipo_despesa_p <> '7'))
and	coalesce(a.ie_tipo_atendimento, coalesce(ie_tipo_atendimento_p, 0))		= coalesce(ie_tipo_atendimento_p, 0)
and	coalesce(a.ie_clinica, coalesce(ie_clinica_p, 0))					= coalesce(ie_clinica_p, 0)
and	coalesce(a.cd_categoria, coalesce(cd_categoria_conv_p, '0'))			= coalesce(cd_categoria_conv_p, '0')
and	coalesce(a.nr_seq_classificacao, coalesce(nr_seq_classificacao_p, '0'))		= coalesce(nr_seq_classificacao_p, '0')
and	coalesce(a.cd_setor_entrada, coalesce(cd_setor_atendimento_p, '0'))		= coalesce(cd_setor_atendimento_p, '0')
and	coalesce(a.cd_setor_exec, coalesce(cd_setor_execucao_p, '0'))			= coalesce(cd_setor_execucao_p, '0')
and	coalesce(a.ie_tipo_atend_tiss, coalesce(ie_tipo_atend_tiss_w,'0'))		= coalesce(ie_tipo_atend_tiss_w,'0')
and	coalesce(a.ie_tipo_atend_proc_tiss, coalesce(ie_tipo_atend_tiss_proc_w,'0'))	= coalesce(ie_tipo_atend_tiss_proc_w,'0')
and	coalesce(a.cd_perfil,coalesce(cd_perfil_w,0))					= coalesce(cd_perfil_w,0)
order by	a.ie_taxas,
	coalesce(cd_setor_exec, 0),
	coalesce(ie_tipo_atendimento, 0),
	coalesce(ie_clinica_p, 0),
	coalesce(cd_categoria, '0'),
	coalesce(cd_setor_entrada, '0'),
	coalesce(a.nr_seq_classificacao,0),
	coalesce(a.ie_tipo_atend_tiss,'0'),
	coalesce(a.ie_tipo_atend_proc_tiss,'0'),
	coalesce(a.cd_perfil,0);


BEGIN

nm_atributo_w			:= upper(nm_atributo_p);

ds_conteudo_w			:= ds_parametros_p;
k 				:= position('#@' in ds_conteudo_w);
ie_tipo_atend_tiss_w		:= ltrim(substr(substr(ds_conteudo_w,1, k - 1),1,255),'0');
ds_conteudo_w			:= substr(ds_conteudo_w, k + 2, 4000);
ie_tipo_atend_tiss_proc_w	:= ltrim(substr(substr(ds_conteudo_w,1, k - 1),1,255),'0');
ds_conteudo_w			:= substr(ds_conteudo_w, k + 2, 4000);



SELECT * FROM tiss_regra_campos_pck.gerar_regra_campos(cd_convenio_w, cd_estabelecimento_p, qt_existentes_w, qt_geradas_w) INTO STRICT qt_existentes_w, qt_geradas_w;


ds_retorno_w := tiss_regra_campos_pck.obter_regra_campos(ie_tipo_guia_p,
							nm_atributo_p,
							cd_convenio_w,
							vl_campo_p,
							ie_tipo_atendimento_p,
							cd_categoria_conv_p,
							ie_tipo_despesa_p,
							vl_max_campo_p,
							cd_estabelecimento_p,
							ie_clinica_p,
							nr_seq_classificacao_p,
							cd_setor_atendimento_p,
							ds_parametros_p,
							cd_setor_execucao_p);




/*
begin
ds_versao_tiss_w		:= tiss_obter_versao(cd_convenio_p,cd_estabelecimento_p);
exception
when others then
	ds_versao_tiss_w	:= null;
end;
*/

/*
