select	1
into	qt_regra_w
from	tiss_regra_campo_conv a
where	a.cd_convenio		= cd_convenio_p
and	a.cd_estabelecimento	= cd_estabelecimento_p
and	rownum 			= 1;
exception
when others then
	qt_regra_w	:= 0;
end;

if	(qt_regra_w > 0) then

	if (obter_se_projeto_versao(0,12,ds_versao_tiss_w,0) = 'N') then
		open c01;
		loop
		fetch c01 into
			ie_envio_w,
			ds_padrao_w,
			vl_permitido_w,
			ds_valor_gerado_w,
			ds_mascara_w;
		exit when c01%notfound;	
			if	(nvl(ie_envio_w, 'E') = 'PG') and (nvl(ds_valor_gerado_w,'X') = nvl(vl_campo_p,'Z')) then
				ds_retorno_w	:= ds_padrao_w;
			end if;
		end loop;
		close c01;	
	else
		open c02;
		loop
		fetch c02 into
			ie_envio_w,
			ds_padrao_w,
			vl_permitido_w,
			ds_valor_gerado_w,
			ds_mascara_w;
		exit when c02%notfound;	
			if	(nvl(ie_envio_w, 'E') = 'PG') and (nvl(ds_valor_gerado_w,'X') = nvl(vl_campo_p,'Z')) then
				ds_retorno_w	:= ds_padrao_w;
			end if;
		end loop;
		close c02;	
	end if;
end if;
*/
return ds_retorno_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
-- REVOKE ALL ON FUNCTION tiss_obter_regra_campo (ie_tipo_guia_p text, nm_atributo_p text, cd_convenio_p text, vl_campo_p text, ie_tipo_atendimento_p bigint, cd_categoria_conv_p text, ie_tipo_despesa_p text, vl_max_campo_p bigint, cd_estabelecimento_p bigint, ie_clinica_p bigint, nr_seq_classificacao_p bigint, cd_setor_atendimento_p bigint, ds_parametros_p text, cd_setor_execucao_p bigint default null) FROM PUBLIC;

