CREATE OR REPLACE FUNCTION tiss_obter_regra_data_guia (cd_estabelecimento_p bigint, cd_convenio_p bigint, ie_tiss_tipo_guia_p text, ie_tipo_atend_tiss_p text, ie_clinica_p bigint) RETURNS varchar AS $body$
DECLARE


ds_retorno_w	varchar(255)	:= null;
ie_data_w	varchar(255);

c01 CURSOR FOR
SELECT	ie_data
from 	tiss_regra_data_guia
where	cd_estabelecimento					= cd_estabelecimento_p
and 	coalesce(cd_convenio,coalesce(cd_convenio_p,0))			= coalesce(cd_convenio_p,0)
and	coalesce(ie_clinica,coalesce(ie_clinica_p,0))			= coalesce(ie_clinica_p,0)
and 	((ie_tipo_data = 'EC' and ie_tiss_tipo_guia_p	= '3') or (ie_tipo_data = 'ESADT' and ie_tiss_tipo_guia_p = '4'))
and	coalesce(ie_tipo_atend_tiss,coalesce(ie_tipo_atend_tiss_p,'X'))	= coalesce(ie_tipo_atend_tiss_p,'X')
order by coalesce(cd_convenio,0),
	coalesce(ie_tipo_atend_tiss,'X'),
	coalesce(ie_clinica,0);


BEGIN

if (ie_tiss_tipo_guia_p IS NOT NULL AND ie_tiss_tipo_guia_p::text <> '') then
	open c01;
	loop
	fetch c01 into
		ie_data_w;
	EXIT WHEN NOT FOUND; /* apply on c01 */
		ds_retorno_w	:= ie_data_w;
	end loop;
	close c01;

end if;

return ds_retorno_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
