CREATE OR REPLACE FUNCTION tiss_obter_regra_lote_guia (cd_estabelecimento_p bigint, cd_convenio_p bigint, ie_tiss_tipo_guia_p text) RETURNS varchar AS $body$
DECLARE


ie_regra_w	varchar(10);


BEGIN

select	coalesce(max(ie_regra),'P')
into STRICT	ie_regra_w
from	tiss_regra_lote_guia
where	cd_estabelecimento	= cd_estabelecimento_p
and	cd_convenio		= cd_convenio_p
and	ie_tiss_tipo_guia	= ie_tiss_tipo_guia_p
and	ie_situacao		= 'A';

return	ie_regra_w;

end;
