CREATE OR REPLACE FUNCTION tiss_obter_regra_mat_opm ( cd_convenio_p bigint, cd_estabelecimento_p bigint, cd_material_p bigint, cd_classe_material_p bigint, cd_subgrupo_material_p bigint, cd_grupo_material_p bigint, ie_tipo_atendimento_p bigint, vl_material_unitario_p bigint) RETURNS varchar AS $body$
DECLARE


ie_opm_w	varchar(10);

c01 CURSOR FOR
SELECT	ie_opm
from	tiss_regra_mat_opm a
where	cd_convenio		= cd_convenio_p
and	cd_estabelecimento	= cd_estabelecimento_p
and	coalesce(cd_material, coalesce(cd_material_p,0))				= coalesce(cd_material_p,0)
and	coalesce(cd_classe_material, coalesce(cd_classe_material_p, 0))		= coalesce(cd_classe_material_p, 0)
and	coalesce(cd_subgrupo_material, coalesce(cd_subgrupo_material_p, 0))	= coalesce(cd_subgrupo_material_p, 0)
and	coalesce(cd_grupo_material, coalesce(cd_grupo_material_p, 0))		= coalesce(cd_grupo_material_p, 0)
and	coalesce(ie_tipo_atendimento, coalesce(ie_tipo_atendimento_p, 0))		= coalesce(ie_tipo_atendimento_p,0)
and	coalesce(vl_material_unitario_p,0) between coalesce(a.vl_material_min,0)	and	coalesce(a.vl_material_max,999999999)
order by coalesce(cd_material,0),
	coalesce(cd_classe_material,0),
	coalesce(cd_subgrupo_material, 0),
	coalesce(cd_grupo_material, 0),
	coalesce(ie_tipo_atendimento,0);


BEGIN

open c01;
loop
fetch c01 into
	ie_opm_w;
EXIT WHEN NOT FOUND; /* apply on c01 */
	ie_opm_w := ie_opm_w;
end loop;
close c01;

return	coalesce(ie_opm_w, 'N');

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
