SET client_encoding TO 'UTF8';





CREATE OR REPLACE FUNCTION tiss_obter_regra_qtde_mat (nr_seq_material_p bigint) RETURNS varchar AS $body$
DECLARE


cd_material_w		bigint;
cd_estabelecimento_w	bigint;
cd_convenio_w		bigint;
cd_grupo_material_w	bigint;
cd_subgrupo_material_w	bigint;
cd_classe_material_w	bigint;
ie_regra_w		varchar(10);
ie_regra_convenio_w	varchar(10);
ds_retorno_w		varchar(10);

c01 CURSOR FOR
SELECT	ie_conversao_qtde_mat
from	tiss_regra_quantidade_mat a
where	a.cd_estabelecimento					= cd_estabelecimento_w
and	coalesce(a.cd_convenio,coalesce(cd_convenio_w,0))			= coalesce(cd_convenio_w,0)
and	coalesce(a.cd_grupo_material,coalesce(cd_grupo_material_w,0))		= coalesce(cd_grupo_material_w,0)
and	coalesce(a.cd_subgrupo_material,coalesce(cd_subgrupo_material_w,0))	= coalesce(cd_subgrupo_material_w,0)
and	coalesce(a.cd_classe_material,coalesce(cd_classe_material_w,0))		= coalesce(cd_classe_material_w,0)
and	coalesce(a.cd_material,coalesce(cd_material_w,0))			= coalesce(cd_material_w,0)
and 	coalesce(a.ie_situacao,'A') = 'A'
order by
	coalesce(a.cd_material,0),
	coalesce(a.cd_classe_material,0),
	coalesce(a.cd_subgrupo_material,0),
	coalesce(a.cd_grupo_material,0),
	coalesce(a.cd_convenio,0);


BEGIN

select	max(cd_material),
	max(cd_estabelecimento),
	max(cd_convenio)
into STRICT	cd_material_w,
	cd_estabelecimento_w,
	cd_convenio_w
from	conta_paciente b,
	material_atend_paciente a
where	a.nr_interno_conta	= b.nr_interno_conta
and	a.nr_sequencia		= nr_seq_material_p;

begin
select	cd_grupo_material,
	cd_subgrupo_material,
	cd_classe_material
into STRICT	cd_grupo_material_w,
	cd_subgrupo_material_w,
	cd_classe_material_w
from	estrutura_material_v
where	cd_material = cd_material_w  LIMIT 1;
exception
when others then
	cd_grupo_material_w	:= null;
	cd_subgrupo_material_w	:= null;
	cd_classe_material_w	:= null;
end;

select	coalesce(max(ie_conversao_qtde_mat), 'M')
into STRICT	ie_regra_w
from	tiss_parametros_convenio
where	cd_convenio		= cd_convenio_w
and	cd_estabelecimento	= cd_estabelecimento_w;

ds_retorno_w	:= ie_regra_w;

if (ie_regra_w = 'CR') then

	open C01;
	loop
	fetch C01 into
		ie_regra_convenio_w;
	EXIT WHEN NOT FOUND; /* apply on C01 */
		ds_retorno_w	:= ie_regra_convenio_w;
	end loop;
	close C01;

	if (coalesce(ie_regra_convenio_w::text, '') = '') then
		ds_retorno_w	:= 'M';
	end if;

end if;

return	ds_retorno_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
