CREATE OR REPLACE FUNCTION tiss_obter_regra_qtde_proc (nr_seq_procedimento_p bigint) RETURNS varchar AS $body$
DECLARE



cd_estabelecimento_w	bigint;
cd_convenio_w		bigint;
ie_regra_w		varchar(10);
ie_regra_convenio_w	varchar(10);
ds_retorno_w		varchar(10);
cd_procedimento_w		tiss_regra_quantidade_mat.cd_procedimento%type;
ie_origem_proced_w	tiss_regra_quantidade_mat.ie_origem_proced%type;
cd_grupo_proc_w		tiss_regra_quantidade_mat.cd_grupo_proc%type;
cd_area_procedimento_w	tiss_regra_quantidade_mat.cd_area_procedimento%type;
cd_especialidade_w	tiss_regra_quantidade_mat.cd_especialidade%type;



c01 CURSOR FOR
SELECT	a.ie_conversao_qtde_mat
from	tiss_regra_quantidade_mat a
where	a.cd_estabelecimento					= cd_estabelecimento_w
and	coalesce(a.cd_convenio,coalesce(cd_convenio_w,0))			= coalesce(cd_convenio_w,0)
and	coalesce(a.cd_procedimento,coalesce(cd_procedimento_w,0))		= coalesce(cd_procedimento_w,0)
and	coalesce(a.ie_origem_proced,coalesce(ie_origem_proced_w,0))		= coalesce(ie_origem_proced_w,0)
and	coalesce(a.cd_area_procedimento,coalesce(cd_area_procedimento_w,0))	= coalesce(cd_area_procedimento_w,0)
and 	coalesce(a.cd_especialidade,coalesce(cd_especialidade_w,0))		= coalesce(cd_especialidade_w,0)
and	coalesce(a.cd_grupo_proc,coalesce(cd_grupo_proc_w,0))			= coalesce(cd_grupo_proc_w,0)
and 	coalesce(a.ie_situacao,'A') = 'A'
order by
	coalesce(a.cd_procedimento,0),
	coalesce(a.cd_grupo_proc,0),
	coalesce(a.cd_especialidade,0),
	coalesce(a.cd_area_procedimento,0),
	coalesce(a.cd_convenio,0);


BEGIN

select	max(cd_procedimento),
	max(ie_origem_proced),
	max(cd_convenio),
	max(cd_estabelecimento)
into STRICT	cd_procedimento_w,
	ie_origem_proced_w,
	cd_convenio_w,
	cd_estabelecimento_w
from	conta_paciente b,
	procedimento_paciente a
where	a.nr_interno_conta	= b.nr_interno_conta
and	a.nr_sequencia		= nr_seq_procedimento_p;

begin
select	cd_area_procedimento,
	cd_especialidade,
	cd_grupo_proc
into STRICT	cd_area_procedimento_w,
	cd_especialidade_w,
	cd_grupo_proc_w
from	estrutura_procedimento_v
where	cd_procedimento = cd_procedimento_w  LIMIT 1;
exception
when others then
	cd_especialidade_w	:= null;
	cd_grupo_proc_w		:= null;
	cd_area_procedimento_w	:= null;
end;

open C01;
loop
fetch C01 into
	ie_regra_convenio_w;
EXIT WHEN NOT FOUND; /* apply on C01 */
	ds_retorno_w	:= ie_regra_convenio_w;
end loop;
close C01;

if (coalesce(ie_regra_convenio_w::text, '') = '') then
	ds_retorno_w	:= 'M';
end if;


return	ds_retorno_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
