CREATE OR REPLACE FUNCTION tiss_obter_regra_senha (cd_estabelecimento_p bigint, cd_convenio_p bigint, ie_tipo_atend_tiss_p text, ie_tiss_tipo_guia_p text, ie_senha_guia_p text, cd_senha_p text) RETURNS varchar AS $body$
DECLARE


ie_senha_guia_regra_w	varchar(255);
ds_retorno_w		varchar(255);
ie_senha_guia_w		varchar(255);

c01 CURSOR FOR
SELECT	coalesce(ie_senha_guia,'N')
from	tiss_regra_senha
where	cd_estabelecimento						= cd_estabelecimento_p
and	coalesce(cd_convenio, coalesce(cd_convenio_p,0))				= coalesce(cd_convenio_p,0)
and	coalesce(ie_tipo_atend_tiss, coalesce(ie_tipo_atend_tiss_p,'X'))		= coalesce(ie_tipo_atend_tiss_p,'X')
and	coalesce(ie_tipo_guia, coalesce(ie_tiss_tipo_guia_p,'X'))			= coalesce(ie_tiss_tipo_guia_p,'X')
order by
	coalesce(cd_convenio, 0),
	coalesce(ie_tipo_atend_tiss,'X'),
	coalesce(ie_tipo_guia,'X');


BEGIN

open C01;
loop
fetch C01 into
	ie_senha_guia_regra_w;
EXIT WHEN NOT FOUND; /* apply on C01 */
end loop;
close C01;

ds_retorno_w	:= null;
ie_senha_guia_w	:= coalesce(ie_senha_guia_regra_w,ie_senha_guia_p);

if (ie_senha_guia_w in ('P','PM')) then
	ds_retorno_w	:= cd_senha_p;
end if;

return ds_retorno_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
