CREATE OR REPLACE FUNCTION tiss_obter_regra_tipo_proc ( cd_convenio_p bigint, cd_estabelecimento_p bigint, cd_area_p bigint, cd_especialidade_p bigint, cd_grupo_p bigint, cd_procedimento_p bigint) RETURNS varchar AS $body$
DECLARE


ie_tipo_desp_w	varchar(10);

c01 CURSOR FOR
SELECT	ie_tipo_desp
from	tiss_regra_tipo_proc a
where	cd_convenio					= cd_convenio_p
and	cd_estabelecimento					= cd_estabelecimento_p
and	coalesce(cd_procedimento, coalesce(cd_procedimento_p,0))		= coalesce(cd_procedimento_p,0)
and	coalesce(cd_grupo_proc, coalesce(cd_grupo_p,0))			= coalesce(cd_grupo_p,0)
and	coalesce(cd_especialidade, coalesce(cd_especialidade_p,0))		= coalesce(cd_especialidade_p,0)
and	coalesce(cd_area_procedimento, coalesce(cd_area_p,0))		= coalesce(cd_area_p,0)
order by	coalesce(cd_procedimento,0),
	coalesce(cd_grupo_proc,0),
	coalesce(cd_especialidade,0),
	coalesce(cd_area_procedimento,0);

BEGIN

open c01;
loop
fetch c01 into
	ie_tipo_desp_w;
EXIT WHEN NOT FOUND; /* apply on c01 */
	ie_tipo_desp_w := ie_tipo_desp_w;
end loop;
close c01;

return	coalesce(ie_tipo_desp_w,'X');

end;
