CREATE OR REPLACE FUNCTION tiss_obter_regra_valor_mat ( cd_estabelecimento_p bigint, cd_convenio_p bigint, cd_material_p bigint, cd_grupo_material_p bigint, cd_subgrupo_material_p bigint, cd_classe_material_p bigint, vl_material_p bigint, vl_mat_proc_principal_p bigint, cd_setor_atendimento_p bigint, ie_responsavel_cred_p text, vl_unitario_p bigint, qt_material_p bigint, ie_tipo_atendimento_p bigint default null) RETURNS bigint AS $body$
DECLARE


vl_retorno_w		double precision := null;
ie_valor_w		varchar(255);

c01 CURSOR FOR
SELECT	ie_valor
from 	tiss_regra_valor_mat a
where	a.cd_estabelecimento							= cd_estabelecimento_p
and	a.cd_convenio								= cd_convenio_p
and	coalesce(a.cd_material,		coalesce(cd_material_p,0))			= coalesce(cd_material_p,0)
and	coalesce(a.cd_grupo_material, 	coalesce(cd_grupo_material_p,0))		= coalesce(cd_grupo_material_p,0)
and	coalesce(a.cd_subgrupo_material,	coalesce(cd_subgrupo_material_p,0))		= coalesce(cd_subgrupo_material_p,0)
and	coalesce(a.cd_classe_material,	coalesce(cd_classe_material_p,0))		= coalesce(cd_classe_material_p,0)
and	coalesce(a.cd_setor_atendimento, coalesce(cd_setor_atendimento_p,0))		= coalesce(cd_setor_atendimento_p,0)
and	coalesce(a.ie_responsavel_credito, coalesce(ie_responsavel_cred_p,'X'))		= coalesce(ie_responsavel_cred_p,'X')
and     coalesce(a.ie_tipo_atendimento, coalesce(ie_tipo_atendimento_p, 0))               = coalesce(ie_tipo_atendimento_p, 0)
order by	coalesce(a.cd_material,0),
	coalesce(a.cd_grupo_material,0),
	coalesce(a.cd_subgrupo_material,0),
	coalesce(a.cd_classe_material,0),
	coalesce(a.cd_setor_atendimento, 0),
	coalesce(a.ie_responsavel_credito, 0),
	coalesce(a.ie_tipo_atendimento, 0);


BEGIN

open c01;
loop
fetch c01 into
	ie_valor_w;
EXIT WHEN NOT FOUND; /* apply on c01 */
end loop;
close c01;

if (ie_valor_w = 'M') then
	vl_retorno_w	:= vl_material_p;
elsif (ie_valor_w = 'MPP') then
	vl_retorno_w	:= vl_mat_proc_principal_p;
elsif (ie_valor_w = 'Z') then
	vl_retorno_w	:= 0;
elsif (ie_valor_w = 'MU') then
	vl_retorno_w	:= vl_unitario_p * qt_material_p;
end if;

return	vl_retorno_w;

end	;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
