CREATE OR REPLACE FUNCTION tiss_obter_sequence_guia (nr_interno_conta_p bigint) RETURNS varchar AS $body$
DECLARE


ds_retorno_w	varchar(255);
nr_sequencias_w	varchar(255) := '';
nr_seq_guia_w	bigint;
i		bigint;

c01 CURSOR FOR
SELECT	nr_sequencia
from	tiss_conta_guia
where	nr_interno_conta	= nr_interno_conta_p;


BEGIN

open c01;
loop
fetch c01 into
	nr_seq_guia_w;
EXIT WHEN NOT FOUND; /* apply on c01 */

	nr_sequencias_w	:= nr_sequencias_w || nr_seq_guia_w || ', ';

end loop;
close c01;

if (nr_sequencias_w IS NOT NULL AND nr_sequencias_w::text <> '') then
	for i in 1..length(nr_sequencias_w) - 2 loop
		ds_retorno_w	:= ds_retorno_w || substr(nr_sequencias_w, i, 1);
	end loop;
end if;

return ds_retorno_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
