SET client_encoding TO 'UTF8';





CREATE OR REPLACE FUNCTION tiss_obter_seq_item_conta (nr_interno_conta_p bigint, cd_autorizacao_p text, cd_item_p bigint, ie_opcao_p text) RETURNS bigint AS $body$
DECLARE


/*
ie_opcao_p
M	Materiais
P	Procedimentos
*/
nr_sequencia_w	bigint;


BEGIN

if (nr_interno_conta_p IS NOT NULL AND nr_interno_conta_p::text <> '') and (cd_item_p IS NOT NULL AND cd_item_p::text <> '') then

	if (ie_opcao_p = 'M') then

		select	max(a.nr_sequencia)
		into STRICT	nr_sequencia_w
		from	material_atend_paciente a
		where	a.nr_interno_conta	= nr_interno_conta_p
		and	a.nr_doc_convenio	= cd_autorizacao_p
		and	a.cd_material		= cd_item_p
		and	a.qt_material		> 0
		and	coalesce(a.cd_motivo_exc_conta::text, '') = '';

	elsif (ie_opcao_p = 'P') then

		select	max(a.nr_sequencia)
		into STRICT	nr_sequencia_w
		from	procedimento_paciente a
		where	a.nr_interno_conta	= nr_interno_conta_p
		and	a.nr_doc_convenio	= cd_autorizacao_p
		and	a.cd_procedimento	= cd_item_p
		and	a.qt_procedimento	> 0
		and	coalesce(a.cd_motivo_exc_conta::text, '') = '';

	end if;

end if;

return	nr_sequencia_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
