		tiss_protocolo_guia b
	where	a.nr_seq_protocolo	= b.nr_seq_protocolo
	and	a.cd_convenio		= cd_convenio_p
	and	a.NR_SEQ_DOC_CONVENIO	= nr_lote_prestador_xml_p;
	
	if (coalesce(nr_seq_protocolo_w::text, '') = '') then
	
		select	max(a.nr_seq_protocolo)
		into STRICT	nr_seq_protocolo_w	
		from	protocolo_convenio a,
			tiss_protocolo_guia b
		where	a.nr_seq_protocolo	= b.nr_seq_protocolo
		and	a.cd_convenio		= cd_convenio_p
		and	a.nr_seq_protocolo	= nr_lote_prestador_xml_p;
	end if;
	
end if;

