CREATE OR REPLACE FUNCTION tiss_obter_se_atend_rn (nr_atendimento_p bigint) RETURNS varchar AS $body$
DECLARE


qt_reg_w			bigint;
ie_cod_usuario_mae_resp_w	varchar(1);
ds_retorno_w			varchar(1);


BEGIN

select	count(*)
into STRICT	qt_reg_w
from	nascimento
where	nr_atend_rn	= nr_atendimento_p
and	nr_atendimento	<> nr_Atend_rn;

if (qt_reg_w	> 0) then
	ds_retorno_w := 'S';
else
	select	count(*)
	into STRICT	qt_reg_w
	from	atendimento_paciente
	where	nr_atendimento	= nr_atendimento_p
	and	(NR_ATENDIMENTO_MAE IS NOT NULL AND NR_ATENDIMENTO_MAE::text <> '');

	if (qt_reg_w	> 0) then
		ds_retorno_w := 'S';
	else
		ds_retorno_w := 'N';
	end if;
end if;

select	coalesce(max(ie_cod_usuario_mae_resp),'N')
into STRICT	ie_cod_usuario_mae_resp_w
from	atend_categoria_convenio
where	nr_seq_interno	= obter_atecaco_atendimento(nr_atendimento)
and	nr_atendimento	= nr_atendimento_p;

if (ie_cod_usuario_mae_resp_w = 'S') then
	ds_retorno_w	:= 'S';
end if;

return	ds_retorno_w;

end;
