CREATE OR REPLACE FUNCTION tiss_obter_se_autor_rn (ie_cod_usuario_mae_resp_p text, nr_sequencia_autor_p bigint, nr_seq_agenda_p bigint, nr_seq_agenda_consulta_p bigint) RETURNS varchar AS $body$
DECLARE


ie_cod_usuario_mae_resp_w		varchar(1);
ds_retorno_w			varchar(1) := 'N';


BEGIN

if (ie_cod_usuario_mae_resp_p IS NOT NULL AND ie_cod_usuario_mae_resp_p::text <> '') then
	ds_retorno_w	:= ie_cod_usuario_mae_resp_p;
else
	begin
	select	ie_cod_usuario_mae_resp
	into STRICT	ie_cod_usuario_mae_resp_w
	from	autorizacao_convenio_tiss
	where	nr_sequencia_autor = nr_sequencia_autor_p
	and	nr_sequencia_autor_p > 0;
	exception
	when others then
		begin
		select	ie_cod_usuario_mae_resp
		into STRICT	ie_cod_usuario_mae_resp_w
		from	agenda_paciente
		where	nr_sequencia = nr_seq_agenda_p
		and	nr_seq_agenda_p > 0;
		exception
		when others then
			begin
			select	ie_cod_usuario_mae_resp
			into STRICT	ie_cod_usuario_mae_resp_w
			from 	agenda_consulta
			where 	nr_sequencia = nr_seq_agenda_consulta_p
			and	nr_seq_agenda_consulta_p > 0;
			exception
			when others then
				ie_cod_usuario_mae_resp_w := 'N';
			end;
		end;
	end;
	ds_retorno_w := coalesce(ie_cod_usuario_mae_resp_w,'N');
end if;

return	ds_retorno_w;

end;
