CREATE OR REPLACE FUNCTION tiss_obter_se_honor_ri (cd_convenio_p bigint, cd_estabelecimento_p bigint, ie_responsavel_credito_p text, ie_tipo_atendimento_p bigint, cd_setor_entrada_p bigint, cd_procedimento_p bigint, ie_origem_proced_p bigint) RETURNS varchar AS $body$
DECLARE


ie_retorno_w		varchar(20);
cd_grupo_proc_w		bigint;
cd_area_procedimento_w	bigint;
cd_especialidade_w	bigint;

c01 CURSOR FOR
SELECT	'S'
from	tiss_regra_honor_ri
where	coalesce(ie_responsavel_credito,	ie_responsavel_credito_p) = ie_responsavel_credito_p
and	coalesce(ie_tipo_atendimento,	ie_tipo_atendimento_p)	= ie_tipo_atendimento_p
and	coalesce(cd_setor_entrada, 		cd_setor_entrada_p)	= cd_setor_entrada_p
and	cd_convenio						= cd_convenio_p
and	cd_estabelecimento					= cd_estabelecimento_p
and	coalesce(cd_procedimento, coalesce(cd_procedimento_p,0))	= coalesce(cd_procedimento_p,0)
and	((ie_origem_proced	= ie_origem_proced_p)	or (coalesce(ie_origem_proced::text, '') = ''))
and	coalesce(cd_area_procedimento, coalesce(cd_area_procedimento_w,0)) = coalesce(cd_area_procedimento_w,0)
and	coalesce(cd_especialidade, coalesce(cd_especialidade_w,0))	= coalesce(cd_especialidade_w,0)
and	coalesce(cd_grupo_proc, coalesce(cd_grupo_proc_w,0))		= coalesce(cd_grupo_proc_w,0)
order by coalesce(cd_procedimento, 0),
	 coalesce(cd_grupo_proc, 0),
	 coalesce(cd_especialidade,0),
	 coalesce(cd_area_procedimento, 0);


BEGIN

select	max(a.cd_grupo_proc),
	max(substr(OBTER_AREA_PROCEDIMENTO(cd_procedimento_p, ie_origem_proced_p),1,200)),
	max(substr(OBTER_ESPECIALIDADE_PROCED(cd_procedimento_p, ie_origem_proced_p),1,200))
into STRICT	cd_grupo_proc_w,
	cd_area_procedimento_w,
	cd_especialidade_w
from	procedimento a
where	cd_procedimento		= cd_procedimento_p
and	ie_origem_proced	= ie_origem_proced_p;

ie_retorno_w		:= 'N';
open c01;
loop
fetch c01 into
	ie_retorno_w;
EXIT WHEN NOT FOUND; /* apply on c01 */
	ie_retorno_w	:= ie_retorno_w;
end loop;
close c01;

return	ie_retorno_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
