CREATE OR REPLACE FUNCTION tiss_obter_se_honor_spsadt (cd_convenio_p bigint, cd_estabelecimento_p bigint, ie_responsavel_credito_p text, ie_tipo_atendimento_p bigint, cd_setor_entrada_p bigint, ie_executor_p text, ie_honorario_p text default null) RETURNS varchar AS $body$
DECLARE


ie_retorno_w		varchar(20);

c01 CURSOR FOR
SELECT	CASE WHEN ie_honorario_p='N' THEN coalesce(IE_GERAR_SOMENTE_GUIA_HONOR,'N')  ELSE 'S' END
from	tiss_regra_honor_spsadt
where	coalesce(ie_responsavel_credito,	ie_responsavel_credito_p) = ie_responsavel_credito_p
and	coalesce(ie_tipo_atendimento,	ie_tipo_atendimento_p)	= ie_tipo_atendimento_p
and	coalesce(cd_setor_entrada, 		cd_setor_entrada_p)	= cd_setor_entrada_p
and	cd_convenio						= cd_convenio_p
and	cd_estabelecimento					= cd_estabelecimento_p
and	((coalesce(ie_medico_exec,'S')	= 'S') or (ie_executor_p			= 'N'));


BEGIN

ie_retorno_w		:= 'N';
open c01;
loop
fetch c01 into
	ie_retorno_w;
EXIT WHEN NOT FOUND; /* apply on c01 */
	ie_retorno_w	:= ie_retorno_w;
end loop;
close c01;

return	ie_retorno_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
