CREATE OR REPLACE FUNCTION tiss_obter_se_mat_conta ( nr_seq_regra_guia_proc_p bigint, nr_interno_conta_p bigint) RETURNS varchar AS $body$
DECLARE

cd_grupo_material_w	bigint;
cd_subgrupo_material_w	bigint;
cd_classe_material_w	bigint;
cd_material_w		bigint;
cd_grupo_material_ww	bigint;
cd_subgrupo_material_ww	bigint;
cd_classe_material_ww	bigint;
cd_material_ww		bigint;
ds_retorno_w		varchar(2);

c01 CURSOR FOR
SELECT	cd_grupo_material,
	cd_subgrupo_material,
	cd_classe_material,
	cd_material
from 	tiss_regra_guia_conta_mat
where	nr_seq_regra		= nr_seq_regra_guia_proc_p;

c02 CURSOR FOR
SELECT	distinct cd_material
from 	material_atend_paciente
where	nr_interno_conta	= nr_interno_conta_p
and	coalesce(cd_motivo_exc_conta::text, '') = '';


BEGIN

ds_retorno_w		:= null;

open c01;
loop
fetch c01 into
	cd_grupo_material_w,
	cd_subgrupo_material_w,
	cd_classe_material_w,
	cd_material_w;
EXIT WHEN NOT FOUND or ds_retorno_w = 'S';  /* apply on c01 */

	ds_retorno_w		:= 'N';

	open c02;
	loop
	fetch c02 into
		cd_material_ww;
	EXIT WHEN NOT FOUND or ds_retorno_w = 'S';  /* apply on c02 */

		select	cd_grupo_material,
			cd_subgrupo_material,
			cd_classe_material
		into STRICT	cd_grupo_material_ww,
			cd_subgrupo_material_ww,
			cd_classe_material_ww
		from 	estrutura_material_v
		where	cd_material	= cd_material_ww;

		if (cd_grupo_material_w IS NOT NULL AND cd_grupo_material_w::text <> '') and (cd_grupo_material_w = cd_grupo_material_ww) then
			ds_retorno_w		:= 'S';
		end if;
		if (cd_subgrupo_material_w IS NOT NULL AND cd_subgrupo_material_w::text <> '') and (cd_subgrupo_material_w = cd_subgrupo_material_ww) then
			ds_retorno_w		:= 'S';
		end if;
		if (cd_classe_material_w IS NOT NULL AND cd_classe_material_w::text <> '') and (cd_classe_material_w = cd_classe_material_ww) then
			ds_retorno_w		:= 'S';
		end if;
		if (cd_material_w IS NOT NULL AND cd_material_w::text <> '') and (cd_material_w = cd_material_ww) then
			ds_retorno_w		:= 'S';
		end if;
	end loop;
	close c02;

end loop;
close c01;

return	ds_retorno_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
