CREATE OR REPLACE FUNCTION tiss_obter_se_pacote_acomod (nr_interno_conta_p bigint) RETURNS varchar AS $body$
DECLARE


ie_pacote_w		varchar(255);
count_w			bigint;


BEGIN

ie_pacote_w := 'N';

select	count(*)
into STRICT	count_w
from	procedimento_paciente a,
	atendimento_pacote b
where	b.nr_atendimento	= a.nr_atendimento
and	a.nr_sequencia		= b.nr_seq_procedimento
and	a.nr_interno_conta	= nr_interno_conta_p;

if (count_w > 0) then

	select	count(*)
	into STRICT	count_w
	from	pacote_tipo_acomod_tiss c,
		atendimento_pacote a,
		procedimento_paciente b
	where	a.nr_atendimento	= b.nr_atendimento
	and	a.nr_seq_procedimento	= b.nr_sequencia
	and	c.nr_seq_tipo_acomod	= a.nr_seq_tipo_acomod
	and	b.nr_interno_conta	= nr_interno_conta_p;

	if (count_w > 0) then
		ie_pacote_w := 'S';
	else
		ie_pacote_w := 'N';
	end if;
else
	ie_pacote_w := 'N';
end if;

return	ie_pacote_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
