CREATE OR REPLACE FUNCTION tiss_obter_se_partic_conv (cd_estabelecimento_p bigint, cd_convenio_p bigint, cd_pessoa_fisica_p text) RETURNS varchar AS $body$
DECLARE


ie_partic_conveniado_w	varchar(20);
ds_retorno_w		varchar(20);


BEGIN

select	coalesce(max(ie_partic_conveniado),'N')
into STRICT	ie_partic_conveniado_w
from	tiss_parametros_convenio
where	cd_convenio		= cd_convenio_p
and	cd_estabelecimento	= cd_estabelecimento_p;

ds_retorno_w			:= 'S';
if (ie_partic_conveniado_w = 'S') then
	select	coalesce(max(ie_conveniado),'N')
	into STRICT	ds_retorno_w
	from	medico_convenio
	where	cd_convenio		= cd_convenio_p
	and	cd_pessoa_fisica	= cd_pessoa_fisica_p;
end if;

return	ds_retorno_w;

end;
