CREATE OR REPLACE FUNCTION tiss_obter_se_pf_escala ( cd_pessoa_fisica_p text, dt_referencia_p timestamp) RETURNS varchar AS $body$
DECLARE


ie_escala_w	varchar(10)	:= null;


BEGIN

select 	max('S')
into STRICT	ie_escala_w
from 	escala_diaria
where 	cd_pessoa_origem	= cd_pessoa_fisica_p
and 	dt_referencia_p	between dt_inicio and dt_fim;

return	ie_escala_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
