CREATE OR REPLACE FUNCTION tiss_obter_se_proc_cirurgico (nr_interno_conta_p bigint, nr_seq_proc_p bigint, nr_sequencia_p bigint) RETURNS bigint AS $body$
DECLARE


/* Function utilizada na ordenação da guia de SADT.
Returno
0 (Zero) 	- Se proc cirurgico
1	- Se não cirurgico
*/
nr_retorno_w		bigint;
cd_procedimento_w	bigint;
ie_origem_proced_w	bigint;
cd_tipo_procedimento_w	smallint;


BEGIN

nr_retorno_w	:= 1;

if (coalesce(nr_seq_proc_p,0) > 0) then

	select	max(cd_procedimento),
		max(ie_origem_proced)
	into STRICT	cd_procedimento_w,
		ie_origem_proced_w
	from	procedimento_paciente a
	where	a.nr_sequencia		= nr_seq_proc_p
	and	a.nr_interno_conta	= nr_interno_conta_p;

else

	begin
	select	max(a.cd_procedimento),
		max(a.ie_origem_proced)
	into STRICT	cd_procedimento_w,
		ie_origem_proced_w
	from	procedimento_paciente a,
		tiss_conta_proc b
	where	a.nr_interno_conta		= b.nr_interno_conta
	and	b.nr_sequencia			= nr_sequencia_p
	and	((a.cd_procedimento		= somente_numero(b.cd_procedimento)) or (a.cd_procedimento		= somente_numero(b.cd_procedimento)) or (a.cd_procedimento_convenio	= somente_numero(b.cd_procedimento)) or (a.cd_procedimento_tuss	= somente_numero(b.cd_procedimento)))
	and	coalesce(a.cd_motivo_exc_conta::text, '') = '';
	exception
	when others then
		cd_procedimento_w	:= null;
		ie_origem_proced_w	:= null;
	end;

end if;

if (coalesce(cd_procedimento_w,0) > 0) then
	select	max(cd_tipo_procedimento)
	into STRICT	cd_tipo_procedimento_w
	from	procedimento
	where	cd_procedimento		= cd_procedimento_w
	and	ie_origem_proced	= ie_origem_proced_w;

	if (cd_tipo_procedimento_w	= 99) then
		nr_retorno_w	:= 0;
	else
		nr_retorno_w	:= 1;
	end if;

end if;

return	nr_retorno_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
