CREATE OR REPLACE FUNCTION tiss_obter_se_proc_principal ( nr_interno_conta_p bigint, cd_procedimento_p text, nr_seq_proc_p bigint) RETURNS varchar AS $body$
DECLARE


ds_retorno_w		varchar(255);
cd_procedimento_w		numeric(20);


BEGIN

cd_procedimento_w	:= somente_numero(cd_procedimento_p);

if (coalesce(nr_seq_proc_p,0) > 0) then
	select	coalesce(max(a.ie_proc_princ_atend),'N')
	into STRICT	ds_retorno_w
	from	procedimento_paciente a
	where	a.nr_sequencia		= nr_seq_proc_p
	and	a.nr_interno_conta	= nr_interno_conta_p
	and	coalesce(a.cd_motivo_exc_conta::text, '') = '';
else
	select	coalesce(max(a.ie_proc_princ_atend),'N')
	into STRICT	ds_retorno_w
	from	procedimento_paciente a
	where	a.nr_interno_conta	= nr_interno_conta_p
	and	a.cd_procedimento		= cd_procedimento_w
	and	coalesce(a.cd_motivo_exc_conta::text, '') = '';

end if;

return ds_retorno_w;

end;
