CREATE OR REPLACE FUNCTION tiss_obter_se_proc_xml (cd_estabelecimento_p bigint, cd_convenio_p bigint, ie_tiss_tipo_guia_p text, ie_resp_credito_p text, cd_procedimento_p bigint, ie_origem_proced_p bigint) RETURNS varchar AS $body$
DECLARE


cd_area_procedimento_w	bigint;
cd_especialidade_w	bigint;
cd_grupo_proc_w		bigint;
ie_enviar_xml_w		varchar(255);
ie_retorno_w		varchar(255);

c01 CURSOR FOR
SELECT	coalesce(ie_enviar_xml,'S')
from	tiss_regra_proc_xml
where	cd_estabelecimento						= cd_estabelecimento_p
and	cd_convenio						= cd_convenio_p
and	coalesce(ie_tiss_tipo_guia,coalesce(ie_tiss_tipo_guia_p,'X'))			= coalesce(ie_tiss_tipo_guia_p,'X')
and	coalesce(ie_responsavel_credito,coalesce(ie_resp_credito_p,'X'))		= coalesce(ie_resp_credito_p,'X')
and	coalesce(cd_area_procedimento,coalesce(cd_area_procedimento_w,0))		= coalesce(cd_area_procedimento_w,0)
and	coalesce(cd_especialidade,coalesce(cd_especialidade_w,0))			= coalesce(cd_especialidade_w,0)
and	coalesce(cd_grupo_proc,coalesce(cd_grupo_proc_w,0))			= coalesce(cd_grupo_proc_w,0)
and	coalesce(cd_procedimento,coalesce(cd_procedimento_p,0))			= coalesce(cd_procedimento_p,0)
and (coalesce(ie_origem_proced,coalesce(ie_origem_proced_p,0))			= coalesce(ie_origem_proced_p,0) or	(cd_procedimento IS NOT NULL AND cd_procedimento::text <> ''))
order by
	coalesce(ie_tiss_tipo_guia,'X'),
	coalesce(ie_responsavel_credito,'X'),
	coalesce(cd_area_procedimento,0),
	coalesce(cd_especialidade,0),
	coalesce(cd_grupo_proc,0),
	coalesce(cd_procedimento,0);


BEGIN

ie_retorno_w	:= 'S';


select	max(cd_grupo_proc),
	max(cd_especialidade),
	max(cd_area_procedimento)
into STRICT	cd_area_procedimento_w,
	cd_especialidade_w,
	cd_grupo_proc_w
from	estrutura_procedimento_v
where	cd_procedimento = cd_procedimento_p
and	ie_origem_proced = ie_origem_proced_p;


open C01;
loop
fetch C01 into
	ie_enviar_xml_w;
EXIT WHEN NOT FOUND; /* apply on C01 */
	begin
	ie_retorno_w	:= ie_enviar_xml_w;
	end;
end loop;
close C01;

return 	ie_retorno_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
